import React, { Component } from 'react';
import { Link } from 'react-router-dom'; // Import React Router's Link component
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import Menu from '@mui/material/Menu';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
import { withRouter } from 'react-router-dom'; // Import withRouter

const pages = [
    { name: 'Book NOW', link: '/room-Type' },
    { name: 'Facilities', link: '/facilities' },
    { name: 'Contact Us', link: '/about-us' },
];

const settings = [
    { name: 'Profile', link: '/profile' },
    { name: 'Sign In', link: '/signinPage' },
    { name: 'Bookings', link: '/bookings' },
    { name: 'Logout', link: '/logout' },
];

class ResponsiveAppBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            anchorElNav: null,
            anchorElUser: null,
        };
    }

    handleOpenNavMenu = (event) => {
        this.setState({ anchorElNav: event.currentTarget });
    };

    handleOpenUserMenu = (event) => {
        this.setState({ anchorElUser: event.currentTarget });
    };

    handleCloseNavMenu = () => {
        this.setState({ anchorElNav: null });
    };

    handleCloseUserMenu = () => {
        this.setState({ anchorElUser: null });
    };

    handleLogout = () => {
        // Logic to clear user session (e.g., localStorage.clear())
        // Redirect to sign-in page
        this.props.history.push('/signinPage');
    };

    render() {
        const { anchorElNav, anchorElUser } = this.state;

        return (
            <AppBar position="static" sx={{ backgroundColor: 'black' }}>
                <Container maxWidth="xl">
                    <Toolbar disableGutters>
                        {/* Add Logo */}
                        <Box sx={{ display: 'flex', alignItems: 'center', mr: 2 }}>
                            <img
                                src="/logoBlack.png"
                                alt="E-JUST Logo"
                                style={{
                                    padding: '20px 50px',
                                    width: '400px',
                                    height: '80px',
                                    marginRight: '40px',
                                }}
                            />
                            <Typography
                                variant="h6"
                                noWrap
                                component="a"
                                href="#"
                                sx={{
                                    fontFamily: 'monospace',
                                    fontWeight: 700,
                                    color: 'white',
                                    textDecoration: 'none',
                                }}
                            >
                                E-JUST Accommodations
                            </Typography>
                        </Box>

                        {/* Mobile Menu */}
                        <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
                            <IconButton
                                size="large"
                                aria-label="open navigation menu"
                                aria-controls="menu-appbar"
                                aria-haspopup="true"
                                onClick={this.handleOpenNavMenu}
                                color="inherit"
                            >
                                <MenuIcon />
                            </IconButton>
                            <Menu
                                id="menu-appbar"
                                anchorEl={anchorElNav}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                                keepMounted
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'left',
                                }}
                                open={Boolean(anchorElNav)}
                                onClose={this.handleCloseNavMenu}
                                sx={{ display: { xs: 'block', md: 'none' } }}
                            >
                                {pages.map((page) => (
                                    <MenuItem key={page.name} onClick={this.handleCloseNavMenu}>
                                        <Typography textAlign="center">
                                            <Link to={page.link} style={{ textDecoration: 'none', color: 'grey' }}>
                                                {page.name}
                                            </Link>
                                        </Typography>
                                    </MenuItem>
                                ))}
                            </Menu>
                        </Box>

                        {/* Desktop Menu */}
                        <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
                            {pages.map((page) => (
                                <Button
                                    key={page.name}
                                    component={Link}
                                    to={page.link}
                                    sx={{ my: 2, color: 'white', display: 'block', textDecoration: 'none' }}
                                >
                                    {page.name}
                                </Button>
                            ))}
                        </Box>

                        {/* User Menu */}
                        <Box sx={{ flexGrow: 0 }}>
                            <Tooltip title="Open settings">
                                <IconButton onClick={this.handleOpenUserMenu} sx={{ p: 0 }}>
                                    <Avatar alt="User Avatar" src="/static/images/avatar/2.jpg" />
                                </IconButton>
                            </Tooltip>
                            <Menu
                                sx={{ mt: '45px' }}
                                id="menu-appbar"
                                anchorEl={anchorElUser}
                                anchorOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right',
                                }}
                                keepMounted
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right',
                                }}
                                open={Boolean(anchorElUser)}
                                onClose={this.handleCloseUserMenu}
                            >
                                {settings.map((setting) => (
                                    <MenuItem
                                        key={setting.name}
                                        onClick={setting.name === 'Logout' ? this.handleLogout : this.handleCloseUserMenu}
                                    >
                                        <Typography textAlign="center">
                                            <Link
                                                to={setting.link}
                                                style={{ textDecoration: 'none', color: 'grey' }}
                                            >
                                                {setting.name}
                                            </Link>
                                        </Typography>
                                    </MenuItem>
                                ))}
                            </Menu>
                        </Box>
                    </Toolbar>
                </Container>
            </AppBar>
        );
    }
}

// Wrap the component with `withRouter` to have access to `this.props.history`
export default withRouter(ResponsiveAppBar);
