import { Box, Button } from '@mui/material';
import React from 'react';
import SelectLabels from "./components/selectBox";
import CustomSlotPropsCallback from "./components/selectBox";
import WovenImageList from "./components/ImageList";
import { Link } from 'react-router-dom';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                backgroundSize: "cover",
                padding: "15px 30px",
                backgroundRepeat: "no-repeat",
            }}
        >
            {/* Header Section */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '10px',
                    borderRadius: '8px',
                }}
            >
                <h1 style={{ textAlign: 'center', marginTop: '20px' }}>
                    E-Just Hotel Guest House
                </h1>
            </Box>

            <SelectLabels />
            <CustomSlotPropsCallback />

            {/* Description Section */}
            <Box
                sx={{
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    maxWidth: '600px',
                    marginTop: '20px',
                    minHeight: '200px',
                }}
            >
                <h3>Deluxe Double Room</h3>
                <p>
                    Meals can be prepared in the kitchenette, which has an oven, 
                    a microwave, a tea and coffee maker, and a toaster. The spacious 
                    double room features air conditioning, a minibar, a terrace with 
                    a quiet street view, and a private bathroom boasting a bath.
                </p>
            </Box>

            {/* Facilities Section */}
            <Box
                sx={{
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    maxWidth: '600px',
                    marginTop: '20px',
                    minHeight: '200px',
                }}
            >
                <h3>Facilities of E-Just Hotel Borg El Arab</h3>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                    {[ 
                        { icon: "room_service_g.png", text: "Room service" },
                        { icon: "local_parking.png", text: "Free parking" },
                        { icon: "assist_walker_24dp_48752C.png", text: "Facilities for disabled guests" },
                        { icon: "wifi.png", text: "Free Wifi" },
                        { icon: "smoke_free_24dp_48752C.png", text: "Non-smoking rooms" },
                        { icon: "coffee_maker_24dp_48752C.png", text: "Tea/Coffee Maker in All Rooms" },
                        { icon: "free_breakfast_24dp_48752C.png", text: "Breakfast" },
                    ].map((facility, index) => (
                        <li key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                            <img
                                src={`/icons/${facility.icon}`}
                                alt={`${facility.text} Logo`}
                                style={{ marginRight: '10px', width: '35px', height: '35px' }}
                            />
                            {facility.text}
                        </li>
                    ))}
                </ul>
            </Box>

            {/* Image Section */}
            <Box
                sx={{
                    position: 'absolute',
                    top: 280,
                    right: 0,
                    width: '42%',
                    height: '90vh',
                    margin: '10px',
                }}
            >
                <WovenImageList />
            </Box>

            {/* Booking Link */}
            <Box>
                <Link to="/Bookservices">
                    <Button
                        variant="contained"
                        sx={{
                            position: 'absolute',
                            top: 790,
                            right: 80,
                            width: '42%',
                            height: '30vh',
                            backgroundColor: "grey",
                            color: "white",
                            fontSize: "18px",
                        }}
                    >
                        Book Your Activities/Services
                    </Button>
                </Link>
            </Box>
        </Box>
    );
}

export default BookNow;
