import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { useState } from "react";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { TextField, Dialog, DialogTitle, DialogContent, DialogActions, List, ListItem, ListItemText } from "@mui/material";


export default function SelectLabelsSingle() {
    const [roomType, setRoomType] = useState("");
    const [checkInDate, setCheckInDate] = useState(null);
    const [checkOutDate, setCheckOutDate] = useState(null);
    const [guests, setGuests] = useState("");
    const [error, setError] = useState(false);

    const [open, setOpen] = useState(false);
    const [bookings, setBookings] = useState([]);

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
        setGuests('');
        setError(false);
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleCheckInDateChange = (newDate) => {
        setCheckInDate(newDate);
    };

    const handleCheckOutDateChange = (newDate) => {
        setCheckOutDate(newDate);
    };

    const handleSubmit = async () => {
        if (!roomType || !checkInDate || !checkOutDate || !guests) {
            setError(true);
            alert("Please complete all fields");
            return;
        }

        if (checkOutDate.isBefore(checkInDate)) {
            setError(true);
            alert("Check-out date cannot be before check-in date");
            return;
        }

        const userId = localStorage.getItem("userId");
        const requestBody = {
            roomType,
            guests,
            checkInDate: checkInDate.toISOString(),
            checkOutDate: checkOutDate.toISOString(),
            userId,
        };

        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(requestBody),
            });

            const data = await response.json();
            if (response.ok) {
                alert("Booking details submitted successfully!");
            } else {
                alert(data.message || "Failed to submit booking details");
            }
        } catch (error) {
            console.error("Error:", error.message);
            alert(error.message || "Failed to submit booking details");
        }
    };

    const handleShowBookings = async () => {
        const userId = localStorage.getItem("userId");

        if (!userId) {
            alert("User not logged in");
            return;
        }

        try {
            const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
            const data = await response.json();

            if (response.ok) {
                setBookings(data.bookings);
                setOpen(true);
            } else {
                alert(data.message || "Failed to fetch bookings");
            }
        } catch (error) {
            console.error("Error:", error.message);
            alert(error.message || "Failed to fetch bookings");
        }

    };

    return (
        <Box sx={{ maxWidth: 500, margin: "0 auto", paddingTop: 3 }}>
            <FormControl fullWidth error={error}>
                <InputLabel>Room Type</InputLabel>
                <Select value={roomType} label="Room Type" onChange={handleRoomChange}>
                    <MenuItem value="Deluxe Room">Deluxe Room</MenuItem>
                    <MenuItem value="King Room">King Room</MenuItem>
                </Select>
                {error && <FormHelperText>Room type is required</FormHelperText>}
            </FormControl>

            <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DatePicker
                    label="Check-in Date"
                    value={checkInDate}
                    onChange={handleCheckInDateChange}
                    renderInput={(props) => <TextField {...props} fullWidth sx={{ marginTop: 2 }} />}
                />
            </LocalizationProvider>

            <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DatePicker
                    label="Check-out Date"
                    value={checkOutDate}
                    onChange={handleCheckOutDateChange}
                    renderInput={(props) => <TextField {...props} fullWidth sx={{ marginTop: 2 }} />}
                />
            </LocalizationProvider>

            <FormControl sx={{ m: 1, minWidth: 400 }} error={error}>
                <InputLabel id="guests-label">Number of Guests</InputLabel>
                <Select
                    labelId="guests-label"
                    id="guests-select"
                    value={guests}
                    label="Number of Guests"
                    onChange={handleGuestsChange}
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value={1}>1 Guest</MenuItem>
                    <MenuItem value={2}>2 Guests</MenuItem>
                </Select>
                {error && <FormHelperText>Number of guests is required</FormHelperText>}
            </FormControl>

            <Button variant="contained" fullWidth sx={{ marginTop: 3 }} onClick={handleSubmit}>
                Submit
            </Button>

            <Button variant="outlined" fullWidth sx={{ marginTop: 3 }} onClick={handleShowBookings}>
                Show Bookings
            </Button>
            <Dialog open={open} onClose={() => setOpen(false)}>
                <DialogTitle>Your Bookings</DialogTitle>
                <DialogContent>
                    <List>
                        {bookings.map((booking, index) => (
                            <ListItem key={index}>
                                <ListItemText
                                    primary={`Room Type: ${booking.roomType}`}
                                    secondary={`Check-in: ${booking.checkInDate}, Check-out: ${booking.checkOutDate}`}
                                />
                            </ListItem>
                        ))}
                    </List>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setOpen(false)} color="primary">
                        Close
                    </Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
}
