import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  CircularProgress,
  Grid,
  Paper,
  List,
  ListItem,
  ListItemText,
  Divider,
  IconButton,
  Snackbar,
  Alert,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

function UserBookings() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [snackbar, setSnackbar] = useState({ open: false, message: "", severity: "info" });

  const fetchUserBookings = async () => {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      setError("User ID not found.");
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
      const data = await response.json();

      if (response.ok) {
        setBookings(data.bookings || []);
      } else {
        setError(data.message || "Failed to fetch bookings.");
      }
    } catch (err) {
      setError("Failed to connect to the server.");
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteBooking = async (bookingId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/bookings/${bookingId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        setBookings((prevBookings) => prevBookings.filter((booking) => booking._id !== bookingId));
        setSnackbar({ open: true, message: "Booking canceled successfully.", severity: "success" });
      } else {
        const data = await response.json();
        setSnackbar({ open: true, message: data.message || "Failed to cancel booking.", severity: "error" });
      }
    } catch (err) {
      setSnackbar({ open: true, message: "Error connecting to the server.", severity: "error" });
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  useEffect(() => {
    fetchUserBookings();
  }, []);

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6" color="error">
          {error}
        </Typography>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: "url('./background2.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        padding: 4,
      }}
    >
      <Snackbar
        open={snackbar.open}
        autoHideDuration={5000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity}>
          {snackbar.message}
        </Alert>
      </Snackbar>
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={10} md={8}>
          <Paper
            sx={{
              padding: 3,
              backgroundColor: "rgba(255, 255, 255, 0.8)",
            }}
          >
            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
              Your Bookings
            </Typography>
            {bookings.length > 0 ? (
              <List>
                {bookings.map((booking) => (
                  <React.Fragment key={booking._id}>
                    <ListItem>
                      <ListItemText primary="Booking ID" secondary={booking._id} />
                      <IconButton
                        edge="end"
                        color="error"
                        onClick={() => handleDeleteBooking(booking._id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </ListItem>
                    <ListItem>
                      <ListItemText primary="Room Type" secondary={booking.roomType} />
                    </ListItem>
                    <ListItem>
                      <ListItemText
                        primary="Check-In Date"
                        secondary={new Date(booking.checkInDate).toLocaleDateString()}
                      />
                    </ListItem>
                    <ListItem>
                      <ListItemText
                        primary="Check-Out Date"
                        secondary={new Date(booking.checkOutDate).toLocaleDateString()}
                      />
                    </ListItem>
                    <ListItem>
                      <ListItemText primary="Number of Guests" secondary={booking.guests} />
                    </ListItem>
                    <ListItem>
                      <ListItemText primary="Total Price" secondary={`$${booking.totalPrice}`} />
                    </ListItem>
                    <Divider sx={{ marginY: 2 }} />
                  </React.Fragment>
                ))}
              </List>
            ) : (
              <Typography variant="h6" align="center" color="textSecondary">
                No bookings found.
              </Typography>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
}

export default UserBookings;
