import React, { useEffect, useState } from "react";
import {
    Box,
    Typography,
    CircularProgress,
    Grid,
    Paper,
    List,
    ListItem,
    ListItemText,
    IconButton,
    Snackbar,
    Alert,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

// BookingService class to encapsulate all API-related logic
class BookingService {
    constructor(baseURL) {
        this.baseURL = baseURL;
    }

    async fetchBookings(userId) {
        try {
            const response = await fetch(`${this.baseURL}/booking/${userId}`);
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Failed to fetch bookings");
            }
            const data = await response.json();
            return data.bookings;
        } catch (error) {
            throw new Error(error.message);
        }
    }

    async deleteBooking(bookingId) {
        try {
            const response = await fetch(`${this.baseURL}/booking/${bookingId}`, {
                method: "DELETE",
            });
            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Failed to cancel booking");
            }
            return true;
        } catch (error) {
            throw new Error(error.message);
        }
    }
}

function Bookings() {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [bookings, setBookings] = useState([]);
    const [snackbar, setSnackbar] = useState({ open: false, message: "", severity: "info" });

    const bookingService = new BookingService("http://localhost:5000/api");

    const fetchBookings = async () => {
        const userId = localStorage.getItem("userId");

        if (!userId) {
            setError("User ID not found.");
            setLoading(false);
            return;
        }

        try {
            const fetchedBookings = await bookingService.fetchBookings(userId);
            setBookings(fetchedBookings);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async () => {
        // Get the last booking or only booking to delete
        const bookingToDelete = bookings.length > 1 ? bookings[bookings.length - 1] : bookings[0];

        try {
            await bookingService.deleteBooking(bookingToDelete._id);
            setBookings((prevBookings) => prevBookings.filter((booking) => booking._id !== bookingToDelete._id));
            setSnackbar({ open: true, message: "Booking cancelled successfully", severity: "success" });
        } catch (err) {
            setSnackbar({ open: true, message: err.message, severity: "error" });
        }
    };

    const handleCloseSnackbar = () => {
        setSnackbar({ ...snackbar, open: false });
    };

    useEffect(() => {
        fetchBookings();
    }, []);

    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ textAlign: "center", marginTop: 3 }}>
                <Typography variant="h6" color="error">
                    {error}
                </Typography>
            </Box>
        );
    }

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundImage: "url('./background2.jpg')",
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                padding: 4,
            }}
        >
            <Snackbar
                open={snackbar.open}
                autoHideDuration={5000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
            >
                <Alert onClose={handleCloseSnackbar} severity={snackbar.severity}>
                    {snackbar.message}
                </Alert>
            </Snackbar>

            <Grid container spacing={2} justifyContent="center">
                {bookings.length > 0 ? (
                    <Grid item xs={12} sm={8} md={6}>
                        <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
                                Your Bookings
                            </Typography>
                            <List>
                                {bookings.map((booking) => (
                                    <React.Fragment key={booking._id}>
                                        <ListItem>
                                            <ListItemText primary="Booking ID" secondary={booking._id} />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText primary="Room Type" secondary={booking.roomType} />
                                            <IconButton
                                                edge="end"
                                                color="error"
                                                onClick={handleDelete}
                                            >
                                                <DeleteIcon />
                                            </IconButton>
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText
                                                primary="Check-in Date"
                                                secondary={new Date(booking.checkInDate).toLocaleDateString()}
                                            />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText
                                                primary="Check-out Date"
                                                secondary={new Date(booking.checkOutDate).toLocaleDateString()}
                                            />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText primary="Number of Guests" secondary={booking.guests} />
                                        </ListItem>
                                        <ListItem>
                                            <ListItemText primary="Room Price" secondary={`$${booking.totalPrice}`} />
                                        </ListItem>
                                    </React.Fragment>
                                ))}
                            </List>
                        </Paper>
                    </Grid>
                ) : (
                    <Typography variant="h6" align="center" color="textSecondary">
                        No bookings found.
                    </Typography>
                )}
            </Grid>
        </Box>
    );
}

export default Bookings;
