import React, { Component } from 'react';
import { withRouter } from 'react-router-dom'; // Import withRouter for navigation

class SelectLabelsSingle extends Component {
    // Other methods remain the same...

    handleSubmit = async () => {
        const { roomType, checkInDate, checkOutDate, guests } = this.state;

        if (!roomType || !checkInDate || !checkOutDate || !guests) {
            this.setState({ error: true });
            alert("Please complete all fields");
            return;
        }

        if (checkOutDate.isBefore(checkInDate)) {
            this.setState({ error: true });
            alert("Check-out date cannot be before check-in date");
            return;
        }

        const userId = localStorage.getItem("userId");
        const requestBody = {
            roomType,
            guests,
            checkInDate: checkInDate.toISOString(),
            checkOutDate: checkOutDate.toISOString(),
            userId,
        };

        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(requestBody),
            });

            const data = await response.json();
            if (response.ok) {
                alert("Booking details submitted successfully!");
                this.props.history.push('/Bookservices'); // Navigate to the next page
            } else {
                alert(data.message || "Failed to submit booking details");
            }
        } catch (error) {
            console.error("Error:", error.message);
            alert(error.message || "Failed to submit booking details");
        }
    };

    // Rest of the class remains unchanged...
}

export default withRouter(SelectLabelsSingle); // Wrap component with withRouter
