import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';

export default function SelectLabelsDouble() {
    const [guests, setGuests] = React.useState('');
    const [error, setError] = React.useState(false);

    const handleChange = (event) => {
        setGuests(event.target.value);
    };
    const handleSubmit = async () => {
        if (!guests) {
            setError(true);
        } else {
            setError(false);

            // Send data to backend for Deluxe Double Room
            try {
                const response = await fetch('http://localhost:5000/api/submit', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ roomType: 'deluxeDouble', guests }),
                });

                const data = await response.json();

                if (!response.ok) {
                    alert(data.message || 'An error occurred');
                } else {
                    console.log('Success:', data);
                    alert('Room and guests successfully selected!');
                }
            } catch (error) {
                console.error('Error during submission:', error);
                alert('Something went wrong. Please try again later.');
            }
        }
    };


    return (
        <Box

        >
            <FormControl sx={{ m: 1, minWidth: 400, justifyContent: 'center', }}>
                <InputLabel id="demo-simple-select-helper-label">Number of Guests</InputLabel>
                <Select
                    labelId="demo-simple-select-helper-label"
                    id="demo-simple-select-helper"
                    value={guests}
                    label="Double room type"
                    onChange={handleChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value={1}>Deluxe Double Room</MenuItem>

                </Select>
                <FormHelperText>select room Type</FormHelperText>
            </FormControl>
        </Box>
    );
}
