import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import  { useState } from "react";


export default function SelectDoubleRoom() {
    const [roomType, setRoomType] = useState("");
    const [error, setError] = useState(false);

    const handleChange = async (event) => {
        const selectedRoom = event.target.value;
        setRoomType(selectedRoom);

        if (!selectedRoom) {
            setError(true);
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ roomType: selectedRoom, guests: 2 }),
            });

            if (!response.ok) {
                const data = await response.json();
                throw new Error(data.message || "An error occurred");
            }

            const data = await response.json();
            console.log("Success:", data);
            alert("Room type successfully selected!");
        } catch (error) {
            console.error("Error during submission:", error.message);
            alert(error.message || "Something went wrong. Please try again.");
        }
    };

    return (
        <Box

        >
            <FormControl sx={{ m: 1, minWidth: 400, justifyContent: 'center', }}>
                <InputLabel id="demo-simple-select-helper-label">Double Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    onChange={handleChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value={1}>Deluxe Double Room</MenuItem>

                </Select>
                <FormHelperText>{error ? "Please select a room type" : "Select a room type"}</FormHelperText>
            </FormControl>
            
        </Box>
        
    );
}
