// Frontend: Adjusted selectbox.js
import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

export default function SelectLabels() {
    const [roomType, setRoomType] = React.useState('');
    const [guests, setGuests] = React.useState('');
    const [error, setError] = React.useState(false);

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
        setGuests('');
        setError(false);
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = async () => {
        if (!roomType || (roomType === 'kingDeluxe' && !guests)) {
            setError(true);
            return;
        }

        setError(false);
        try {
            const response = await fetch('/api/booking/details', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ roomType, guests: guests || 2 }),
            });

            const data = await response.json();
            if (response.ok) {
                console.log('Booking details saved:', data);
            } else {
                console.error('Error saving booking details:', data.message);
            }
        } catch (error) {
            console.error('Request failed:', error);
        }
    };

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    label="Room Type"
                    onChange={handleRoomChange}
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value="kingDeluxe">Single King Deluxe</MenuItem>
                    <MenuItem value="deluxeDouble">Double Duplex</MenuItem>
                </Select>
                <FormHelperText>Choose your room type</FormHelperText>
            </FormControl>

            {roomType === 'kingDeluxe' && (
                <FormControl sx={{ m: 1, minWidth: 400 }} error={error}>
                    <InputLabel id="guests-label">Number of Guests</InputLabel>
                    <Select
                        labelId="guests-label"
                        id="guests-select"
                        value={guests}
                        label="Number of Guests"
                        onChange={handleGuestsChange}
                    >
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        <MenuItem value={1}>1 Guest</MenuItem>
                        <MenuItem value={2}>2 Guests</MenuItem>
                    </Select>
                    {error && <FormHelperText>Number of guests is required</FormHelperText>}
                </FormControl>
            )}

            <Button onClick={handleSubmit} variant="contained" color="primary">
                Submit
            </Button>
        </Box>
    );
}
