import React, { useState } from 'react';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { Button, Container, TextField } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';

const BRANDING = {
    logo: (
        <img
            src="./ejustLogoSignin.png"
            alt="E-JUST logo"
            style={{ height: 24 }}
        />
    ),
    title: "Admin Portal",
};

export default function AdminSignInRegisterPage() {
    const theme = useTheme();
    const navigate = useNavigate();

    // State variables
    const [isRegister, setIsRegister] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleAdminSignIn = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/admin/signin", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ email, password }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Sign-in failed");
            }

            const data = await response.json();
            localStorage.setItem("adminId", data.adminId);

            navigate("/admin-dashboard");
        } catch (error) {
            console.error("Error during admin sign-in:", error.message);
            alert(error.message); // Display error to the admin
        }
    };

    const handleAdminRegister = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/admin/register", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ name, email, password }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Registration failed");
            }

            alert("Admin registered successfully!");
            setIsRegister(false); // Switch to sign-in view
        } catch (error) {
            console.error("Error during admin registration:", error.message);
            alert(error.message); // Display error to the admin
        }
    };

    return (
        <AppProvider branding={BRANDING} theme={theme}>
            <Container>
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        minHeight: '100vh',
                        gap: theme.spacing(2),
                    }}
                >
                    {isRegister ? (
                        <>
                            {/* Admin Registration Form */}
                            <h2>Admin Registration</h2>
                            <TextField
                                label="Name"
                                variant="outlined"
                                fullWidth
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                sx={{ marginBottom: theme.spacing(2) }}
                            />
                            <TextField
                                label="Email"
                                variant="outlined"
                                fullWidth
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                sx={{ marginBottom: theme.spacing(2) }}
                            />
                            <TextField
                                label="Password"
                                type="password"
                                variant="outlined"
                                fullWidth
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                sx={{ marginBottom: theme.spacing(2) }}
                            />
                            <Button
                                variant="contained"
                                color="primary"
                                fullWidth
                                onClick={handleAdminRegister}
                            >
                                Register as Admin
                            </Button>
                            <Button
                                variant="text"
                                color="secondary"
                                onClick={() => setIsRegister(false)}
                                sx={{ marginTop: theme.spacing(1) }}
                            >
                                Back to Sign-In
                            </Button>
                        </>
                    ) : (
                        <>
                            {/* Admin Sign-In Form */}
                            <h2>Admin Sign-In</h2>
                            <TextField
                                label="Email"
                                variant="outlined"
                                fullWidth
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                sx={{ marginBottom: theme.spacing(2) }}
                            />
                            <TextField
                                label="Password"
                                type="password"
                                variant="outlined"
                                fullWidth
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                sx={{ marginBottom: theme.spacing(2) }}
                            />
                            <Button
                                variant="contained"
                                color="primary"
                                fullWidth
                                onClick={handleAdminSignIn}
                            >
                                Sign In as Admin
                            </Button>
                            <Button
                                variant="text"
                                color="secondary"
                                onClick={() => setIsRegister(true)}
                                sx={{ marginTop: theme.spacing(1) }}
                            >
                                Register as New Admin
                            </Button>
                        </>
                    )}
                </Box>
            </Container>
        </AppProvider>
    );
}
