import { Box, Button, Grid } from '@mui/material';
import React, { useState } from 'react';
import MergedSelectComponent from "./components/";

function Bookservices() {
    
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
            }}
        >
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just services</h1>
                </div>
            </Box>
            <Grid container spacing={2} sx={{ marginTop: '20px' }}>
                {/* Food Selection */}
                <Grid item xs={12} sm={6}>
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                        }}
                    >
                        <h3>Book your food bundle</h3>
                        <MergedSelectComponent
                            type="food"
                            onFoodChange={(value) => setFoodChoice(value)}
                            onCustomPeriodChange={(value) => setCustomPeriod(value)}
                            onMealTypeChange={(value) => setMealType(value)}
                        />
                    </Box>
                </Grid>

                {/* Court Selection */}
                <Grid item xs={12} sm={6}>
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                        }}
                    >
                        <h3>Book your sport court</h3>
                        <MergedSelectComponent
                            type="court"
                            onCourtTypeChange={(value) => setCourtChoice(value)}
                            onCourtTimeChange={(value) => setCourtTime(value)}
                        />
                    </Box>
                </Grid>

                {/* Gym Selection */}
                <Grid item xs={12} sm={6}>
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                        }}
                    >
                        <h3>Book your gym time</h3>
                        <MergedSelectComponent
                            type="gym"
                            onGymTimeChange={(value) => setGymTime(value)}
                        />
                    </Box>
                </Grid>
            </Grid>

            <Box
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    marginTop: '30px',
                }}
            >
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSubmit}
                >
                    Submit All Choices
                </Button>
            </Box>
        </Box>
    );
}

export default Bookservices;
