import React, { useEffect, useState } from 'react';
import { Box, Typography, Button, Container } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ConfirmationPage = () => {
  const [bookingDetails, setBookingDetails] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch booking details from localStorage or API if needed
    const bookingId = localStorage.getItem('bookingId');
    const userId = localStorage.getItem('userId');
    if (bookingId && userId) {
      // You can replace this with an actual API call to fetch booking details if needed
      setBookingDetails({
        bookingId,
        roomType: "Deluxe Room", // Example room type
        guests: 2, // Example number of guests
        checkInDate: "2024-12-29", // Example check-in date
        checkOutDate: "2024-12-31", // Example check-out date
      });
    }
  }, []);

  const handleGoBack = () => {
    navigate('/BookNow'); // Navigate back to the booking page
  };

  return (
    <Container
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundImage: 'url("./confirmation-bg.jpg")', // Replace with your desired background
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        textAlign: 'center',
      }}
    >
      {bookingDetails ? (
        <>
          <Box
            sx={{
              backgroundColor: 'rgba(255, 255, 255, 0.8)',
              padding: 4,
              borderRadius: 2,
              boxShadow: 2,
              width: '100%',
              maxWidth: 500,
            }}
          >
            <Typography variant="h4" gutterBottom>
              Booking Confirmed!
            </Typography>
            <Typography variant="h6" color="primary" gutterBottom>
              Your booking details:
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Booking ID:</strong> {bookingDetails.bookingId}
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Room Type:</strong> {bookingDetails.roomType}
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Guests:</strong> {bookingDetails.guests}
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Check-in Date:</strong> {bookingDetails.checkInDate}
            </Typography>
            <Typography variant="body1" gutterBottom>
              <strong>Check-out Date:</strong> {bookingDetails.checkOutDate}
            </Typography>
            <Button variant="contained" color="primary" onClick={handleGoBack}>
              Go Back to Booking
            </Button>
          </Box>
        </>
      ) : (
        <Typography variant="h6" color="error">
          No booking details found.
        </Typography>
      )}
    </Container>
  );
};

export default ConfirmationPage;
