import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

export default function SelectLabelsDouble() {
    const [guests] = React.useState(2); // Always 2 guests for this room type
    const [roomType, setRoomType] = React.useState('deluxeDouble'); // Default to "deluxeDouble"

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
    };

    const handleSubmit = async () => {
        // Send room type and guests to the backend
        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ roomType, guests }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Error occurred");
            }

            const data = await response.json();
            console.log("Room and guests updated successfully:", data);
        } catch (error) {
            console.error("Error updating room and guests:", error.message);
            alert(error.message || "Failed to update room and guests");
        }
    };

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    label="Room Type"
                    onChange={handleRoomChange}
                >
                    <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
                </Select>
                <FormHelperText>Choose your room type</FormHelperText>
            </FormControl>

            <Button onClick={handleSubmit} variant="contained" color="primary">
                Select
            </Button>
        </Box>
    );
}
