import React, { useState } from 'react';
import {
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Box,
  Container,
  TextField,
} from '@mui/material';

export default function BookNow() {
  const [roomType, setRoomType] = useState('');
  const [guests, setGuests] = useState('');
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [error, setError] = useState(false);

  const handleRoomChange = (event) => {
    setRoomType(event.target.value);
    setGuests('');
    setError(false);
  };

  const handleGuestsChange = (event) => {
    setGuests(event.target.value);
  };

  const handleSubmit = async () => {
    if (!roomType || !guests || !checkInDate || !checkOutDate) {
      setError(true);
    } else {
      setError(false);
      const response = await fetch("http://localhost:5000/api/booking/details", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ roomType, guests, checkInDate, checkOutDate }),
      });

      const data = await response.json();
      if (response.ok) {
        console.log(`Booking details: Room Type: ${roomType}, Guests: ${guests}, Check-In: ${checkInDate}, Check-Out: ${checkOutDate}`);
      } else {
        alert(data.message || 'Failed to submit the booking details');
      }
    }
  };

  return (
    <Container sx={{ py: 5 }}>
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
        <FormControl fullWidth sx={{ maxWidth: 300 }}>
          <InputLabel>Room Type</InputLabel>
          <Select value={roomType} onChange={handleRoomChange} label="Room Type">
            <MenuItem value="kingDeluxe">King Deluxe</MenuItem>
            <MenuItem value="queenStandard">Queen Standard</MenuItem>
          </Select>
        </FormControl>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <FormControl fullWidth sx={{ maxWidth: 300 }}>
          <InputLabel>Guests</InputLabel>
          <Select value={guests} onChange={handleGuestsChange} label="Guests">
            <MenuItem value={1}>1</MenuItem>
            <MenuItem value={2}>2</MenuItem>
          </Select>
        </FormControl>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <TextField
          label="Check-In Date"
          type="date"
          InputLabelProps={{ shrink: true }}
          value={checkInDate}
          onChange={(e) => setCheckInDate(e.target.value)}
          sx={{ maxWidth: 300 }}
        />
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <TextField
          label="Check-Out Date"
          type="date"
          InputLabelProps={{ shrink: true }}
          value={checkOutDate}
          onChange={(e) => setCheckOutDate(e.target.value)}
          sx={{ maxWidth: 300 }}
        />
      </Box>

      {error && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 1, color: 'red' }}>
          Please fill in all fields.
        </Box>
      )}

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <Button variant="contained" onClick={handleSubmit}>
          Submit
        </Button>
      </Box>
    </Container>
  );
}
