import React, { useEffect, useState } from "react";
import { Box, Typography, CircularProgress, Grid, Paper, List, ListItem, ListItemText, Snackbar, IconButton } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { Alert } from "@mui/material";

function Bookings() {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [bookings, setBookings] = useState([]);
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState("");

    useEffect(() => {
        const fetchBookings = async () => {
            const userId = localStorage.getItem("userId");

            if (!userId) {
                setError("User ID not found.");
                setLoading(false);
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
                const data = await response.json();
                console.log("Bookings fetched:", data.bookings);

                if (response.ok) {
                    setBookings(data.bookings);
                } else {
                    setSnackbarMessage(data.message || "Failed to fetch bookings");
                    setSnackbarOpen(true);
                }
            } catch (err) {
                console.error("Error fetching bookings:", err);
                setError("Failed to fetch bookings data.");
            } finally {
                setLoading(false);
            }
        };

        fetchBookings();

        // Refresh bookings every 30 seconds
        const interval = setInterval(fetchBookings, 30000);

        return () => clearInterval(interval); // Cleanup interval on unmount
    }, []);

    const handleCancelBooking = async (bookingId) => {
        console.log("Booking ID passed to handleCancelBooking:", bookingId);

        if (!bookingId) {
            setSnackbarMessage("Invalid booking ID.");
            setSnackbarOpen(true);
            return;
        }

        try {
            const response = await fetch(
                `http://localhost:5000/api/bookings/${bookingId}`,
                { method: "DELETE" }
            );

            if (response.ok) {
                setBookings((prevBookings) =>
                    prevBookings.filter((booking) => booking._id !== bookingId)
                );
                setSnackbarMessage("Booking canceled successfully.");
                setSnackbarOpen(true);
            } else {
                const data = await response.json();
                setSnackbarMessage(data.message || "Failed to cancel booking. Please try again.");
                setSnackbarOpen(true);
            }
        } catch (err) {
            console.error("Error canceling booking:", err);
            setSnackbarMessage("Error connecting to the server. Please try again later.");
            setSnackbarOpen(true);
        }
    };

    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ textAlign: "center", marginTop: 3 }}>
                <Typography variant="h6" color="error">
                    {error}
                </Typography>
            </Box>
        );
    }

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundImage: "url('./background2.jpg')",
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                padding: 4,
            }}
        >
            <Grid container spacing={2} justifyContent="center">
                {/* Bookings Section */}
                {bookings.length > 0 ? (
                    <Grid item xs={12} sm={8} md={6}>
                        <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
                                Your Bookings
                            </Typography>
                            <List>
                                {bookings.map((booking) => {
                                    console.log("Booking object:", booking); // Log each booking to check _id
                                    return (
                                        <React.Fragment key={booking._id}>
                                            <IconButton
                                                edge="end"
                                                aria-label="delete"
                                                onClick={() => {
                                                    console.log(`Deleting booking with ID: ${booking._id}`);
                                                    handleCancelBooking(booking._id);
                                                }}
                                            >
                                                <DeleteIcon />
                                            </IconButton>
                                            <ListItem>
                                                <ListItemText primary="Room Type" secondary={booking.roomType} />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText
                                                    primary="Check-in Date"
                                                    secondary={new Date(booking.checkInDate).toLocaleDateString()}
                                                />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText
                                                    primary="Check-out Date"
                                                    secondary={new Date(booking.checkOutDate).toLocaleDateString()}
                                                />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText primary="Number of Guests" secondary={booking.guests} />
                                            </ListItem>
                                            <ListItem>
                                                <ListItemText primary="Room Price" secondary={`$${booking.totalPrice}`} />
                                            </ListItem>
                                        </React.Fragment>
                                    );
                                })}
                            </List>
                        </Paper>
                    </Grid>
                ) : (
                    <Typography variant="h6" align="center" color="textSecondary">
                        No bookings found.
                    </Typography>
                )}
            </Grid>

            {/* Snackbar for success/error messages */}
            <Snackbar
                open={snackbarOpen}
                autoHideDuration={6000}
                onClose={() => setSnackbarOpen(false)}
            >
                <Alert onClose={() => setSnackbarOpen(false)} severity="success">
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </Box>
    );
}

export default Bookings;
