import React, { useEffect, useState } from "react";
import { Box, Typography, CircularProgress, Grid, Paper, List, ListItem, ListItemText } from "@mui/material";

function Profile() {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [userInfo, setUserInfo] = useState(null);
    const [bookingDetails, setBookingDetails] = useState(null);
    const [services, setServices] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const userId = localStorage.getItem("userId");
            const bookingId = localStorage.getItem("bookingId");

            if (!userId || !bookingId) {
                setError("User or booking ID not found.");
                setLoading(false);
                return;
            }

            try {
                const userResponse = await fetch(`http://localhost:5000/api/register/${userId}`);
                const userData = await userResponse.json();
                if (!userResponse.ok) throw new Error();

                const bookingResponse = await fetch(`http://localhost:5000/api/booking/${userId}`);
                const bookingData = await bookingResponse.json();
                if (bookingResponse.ok) {
                    setBookingDetails(bookingData.bookings);
                } else{
                    alert(bookingData.message || "Failed to fetch bookings");
                }

                const servicesResponse = await fetch(`http://localhost:5000/api/services/${bookingId}`);
                const servicesData = await servicesResponse.json();
                if (!servicesResponse.ok) throw new Error();

                setUserInfo(userData);
                setBookingDetails(bookingData.bookings[bookingData.bookings.length - 1]); // Latest booking
                setServices(servicesData.services || []);
            } catch {
                setError("Failed to fetch profile data.");
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
                <CircularProgress />
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ textAlign: "center", marginTop: 3 }}>
                <Typography variant="h6" color="error">
                    {error}
                </Typography>
            </Box>
        );
    }

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundImage: "url('./background2.jpg')",
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                padding: 4,
            }}
        >
            <Grid container spacing={2} justifyContent="center">
                {/* User Info Section */}
                <Grid item xs={12} sm={8} md={6}>
                    <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                        <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
                            User Profile
                        </Typography>

                        <Typography variant="h6" color="primary" gutterBottom>
                            User Information
                        </Typography>
                        <List>
                            <ListItem>
                                <ListItemText primary="Name" secondary={userInfo.Name} />
                            </ListItem>
                            <ListItem>
                                <ListItemText primary="Email" secondary={userInfo.email} />
                            </ListItem>
                            
                        </List>
                    </Paper>
                </Grid>

                {/* Booking Details Section */}
                {bookingDetails && (
                    <Grid item xs={12} sm={8} md={6}>
                        <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                            <Typography variant="h6" color="primary" gutterBottom>
                                Your Bookings
                            </Typography>
                            <List>
                                
                                <ListItem>
                                    <ListItemText primary="Room Type" secondary={bookingDetails.roomType} />
                                </ListItem>
                                <ListItem>
                                    <ListItemText
                                        primary="Check-in Date"
                                        secondary={new Date(bookingDetails.checkInDate).toLocaleDateString()}
                                    />
                                </ListItem>
                                <ListItem>
                                    <ListItemText
                                        primary="Check-out Date"
                                        secondary={new Date(bookingDetails.checkOutDate).toLocaleDateString()}
                                    />
                                </ListItem>
                                <ListItem>
                                    <ListItemText primary="Number of Guests" secondary={bookingDetails.guests} />
                                </ListItem>
                                <ListItem>
                                    <ListItemText primary="Room Price" secondary={`$${bookingDetails.totalPrice}`} />
                                </ListItem>
                            </List>
                        </Paper>
                    </Grid>
                )}

                {/* Services Section */}
                {services.length > 0 && (
                    <Grid item xs={12} sm={8} md={6}>
                        <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                            <Typography variant="h6" color="primary" gutterBottom>
                                Services
                            </Typography>
                            <List>
                                {services.map((service, index) => (
                                    <ListItem key={index}>
                                        <Box>
                                            <Typography variant="body1">
                                                <strong>Food:</strong> {service.food.bundle || "No Food Bundle"} - {service.food.mealType || "N/A"} - ${service.food.price || "N/A"}
                                            </Typography>
                                            <Typography variant="body1">
                                                <strong>Court:</strong> {service.court.type || "No Court Type"} - {service.court.time || "N/A"} - {service.court.date ? new Date(service.court.date).toLocaleDateString() : "N/A"}
                                            </Typography>
                                            <Typography variant="body1">
                                                <strong>Gym:</strong> {service.gym.time || "N/A"}
                                            </Typography>
                                        </Box>
                                    </ListItem>
                                ))}
                            </List>
                        </Paper>
                    </Grid>
                )}
            </Grid>
        </Box>
    );
}

export default Profile;
