import { Box } from '@mui/material';
import React from 'react';
import { SelectLabelsDouble } from './components/selectBoxDoubleRoom';
import { WovenImageListDouble } from './components/ImageListDoubleRoom';
import { Button } from '@mui/material';

function BookNowDouble() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
            }}
        >
            {/* Header Section */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Flexbox layout: Booking Form + Image Gallery */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row',
                    gap: '20px',
                }}
            >
                {/* Left Column: Booking Form */}
                <Box
                    sx={{
                        flex: 2,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'flex-start',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    {/* Select Labels (Booking Form) */}
                    <SelectLabelsDouble />

                    {/* Room Description */}
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'flex-start',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                            width: '100%',
                            marginTop: '20px',
                        }}
                    >
                        <h3>Deluxe Double Room</h3>
                        <p>
                            The spacious double room provides air conditioning, a mini-bar, a terrace with a quiet street view,
                            as well as a private bathroom featuring a bath. The unit offers 2 beds.
                        </p>
                    </Box>

                    {/* Facilities Section */}
                    <Box
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            alignItems: 'flex-start',
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                            width: '100%',
                            marginTop: '20px',
                        }}
                    >
                        <h3>Facilities of E-Just Hotel Borg El Arab</h3>
                        <p>
                            Most popular facilities
                            <ul style={{ listStyleType: 'none', padding: 0, margin: 0 }}>
                                <li>Room service</li>
                                <li>Free parking</li>
                                <li>Facilities for disabled guests</li>
                                <li>Free Wifi</li>
                                <li>Non-smoking rooms</li>
                                <li>Tea/Coffee Maker in All Rooms</li>
                                <li>Breakfast</li>
                            </ul>
                        </p>
                    </Box>
                </Box>

                {/* Right Column: Image Gallery */}
                <Box
                    sx={{
                        flex: 1,
                        display: 'flex',
                        flexDirection: 'column',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>Room Images</h3>
                    <WovenImageListDouble />
                </Box>
            </Box>
        </Box>
    );
}

export default BookNowDouble;
