import * as React from 'react';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { Button, Container } from '@mui/material';
import { useTheme } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';
import Box from '@mui/material/Box';

const providers = [{ id: 'credentials', name: 'Credentials' }];

const BRANDING = {
    logo: (
        <img
            src="./ejustLogoSignin.png"
            alt="E-JUST logo"
            style={{ height: 24 }}
        />
    ),
    title: "to E-JUST Accommodation",
};

export default function BrandingSignInPage() {
    const theme = useTheme();
    const navigate = useNavigate();

    // Use React state to manage email and password inputs
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [isAdmin, setIsAdmin] = React.useState(false);
    const signIn = async () => {
        try {
            console.log("Email:", email, "Password:", password); // Log email and password
            const response = await fetch("http://localhost:5000/api/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ email, password }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Unknown error occurred");
            }

            const data = await response.json();
            console.log("Login successful:", data);
            localStorage.setItem("userId", data.userId);            
            // Redirect based on role
            if (isAdmin) {
                navigate("/admin-dashboard");
            } else {
                navigate("/room-Type");
            }

        } catch (error) {
            console.error("Error during login:", error.message);
            alert(error.message || "Login failed. Please try again.");
        }
    };

    return (
        <AppProvider branding={BRANDING} theme={theme}>
            <Container>
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center',
                        justifyContent: 'center',
                        minHeight: '100vh', // Ensure vertical centering
                        gap: theme.spacing(2), // Reduce gap between SignInPage and button
                    }}
                >
                    {/* Admin Checkbox */}
                    <Box
                    >
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={() => navigate("/admin-signin")}
                            sx={{
                                marginTop: theme.spacing(2),
                                padding: theme.spacing(1.5, 3),
                            }}
                        >
                            Register as Admin
                        </Button>
                    </Box>
                                
                    <SignInPage
                        signIn={async (provider, email, password) => {
                            if (!email || !password) {
                                alert("Email and password are required");
                                return;
                            }
                            await signIn();
                        }}
                        providers={providers}
                        slotProps={{
                            emailField: {
                                value: email,
                                onChange: (e) => setEmail(e.target.value),
                            },
                            passwordField: {
                                value: password,
                                onChange: (e) => setPassword(e.target.value),
                            },
                        }}
                    />
                    {/* Create Account Button */}
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={() => navigate("/register")}
                        sx={{
                            marginTop: 0,         // Remove extra space
                            padding: theme.spacing(1.5, 3), // Adjust padding for a balanced look
                        }}
                    >
                        Create Account
                    </Button>
                </Box>
            </Container>
        </AppProvider>

    );
}
