import React from 'react';
import { useLocation } from 'react-router-dom';

const ConfirmationPage = () => {
    const { state } = useLocation(); // Access the booking details passed from the previous page
    const { bookingDetails } = state || {};

    return (
        <div>
            <h2>Booking Confirmation</h2>
            <p><strong>Booking ID:</strong> {bookingDetails?.bookingId}</p>
            <p><strong>User ID:</strong> {bookingDetails?.userId}</p>
            <p><strong>Food Bundle:</strong> {bookingDetails?.food.bundle}</p>
            <p><strong>Meal Type:</strong> {bookingDetails?.food.mealType}</p>
            <p><strong>Court Type:</strong> {bookingDetails?.court.type}</p>
            <p><strong>Court Time:</strong> {bookingDetails?.court.time}</p>
            <p><strong>Court Date:</strong> {bookingDetails?.court.date?.format('YYYY-MM-DD')}</p>
            <p><strong>Gym Time:</strong> {bookingDetails?.gym.time}</p>
            <p><strong>Gym Date:</strong> {bookingDetails?.gym.date?.format('YYYY-MM-DD')}</p>
        </div>
    );
};

export default ConfirmationPage;
