import React from 'react';
import { Box, Button } from '@mui/material';
import SelectLabelsSingle from './components/SelectBox'; // Updated to Single
import { WovenImageListDouble } from './components/WovenImageList';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: '100vh',
                backgroundColor: 'white',
                padding: '15px 30px',
            }}
        >
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '10px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '10px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Flexbox layout: Booking Table + Image Gallery */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row',
                    gap: '20px',
                }}
            >
                {/* Booking Form */}
                <Box
                    sx={{
                        flex: 2,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'flex-start',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                        height: 'auto',
                        minHeight: '200px',
                    }}
                >
                    <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>
                        Book Your Stay
                    </h3>
                    <SelectLabelsSingle />
                </Box>

                {/* Image Gallery */}
                <Box
                    sx={{
                        flex: 1,
                        display: 'flex',
                        flexDirection: 'column',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>
                        Room Images
                    </h3>
                    <WovenImageListDouble />
                </Box>
            </Box>
        </Box>
    );
}

export default BookNow;
