import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
export default function SelectLabelsDouble() {
    const [roomType, setRoomType] = React.useState('deluxeDouble'); // Set to deluxeDouble by default

    const handleChange = (event) => {
        setRoomType(event.target.value);
    };

    const handleSubmit = () => {
        // Make a fetch request to the backend without axios
        fetch('/booking/details', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ roomType, guests: 2 }), // Automatically set guests to 2 for double rooms
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    label="Room Type"
                    onChange={handleChange}
                >
                    <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
                </Select>
                <FormHelperText>Double room type selected</FormHelperText>
            </FormControl>

            <Button onClick={handleSubmit} variant="contained" color="primary">
                Select
            </Button>
        </Box>
    );
}
