import { Box } from '@mui/material';
import React from 'react';
import { SelectLabelsSingle } from './components/selectBox';
// import { WovenImageListDouble } from './components/ImageListDoubleRoom';
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';

export default function BookNow() {


  return (
    <Container sx={{ py: 5 }}>
      <Box sx={{ display: 'flex', justifyContent: 'center' }}>
        <FormControl fullWidth error={error} sx={{ maxWidth: 300 }}>
          <InputLabel>Room Type</InputLabel>
          <Select value={roomType} onChange={handleRoomChange} label="Room Type">
            <MenuItem value="kingDeluxe">King Deluxe</MenuItem>
            <MenuItem value="queenStandard">Queen Standard</MenuItem>
          </Select>
          {error && <FormHelperText>All fields are required</FormHelperText>}
        </FormControl>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <FormControl fullWidth sx={{ maxWidth: 300 }}>
          <InputLabel>Guests</InputLabel>
          <Select value={guests} onChange={handleGuestsChange} label="Guests">
            <MenuItem value={1}>1</MenuItem>
            <MenuItem value={2}>2</MenuItem>
            <MenuItem value={3}>3</MenuItem>
          </Select>
        </FormControl>
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <input
          type="date"
          value={checkInDate}
          onChange={handleCheckInDateChange}
          style={{ width: '100%', maxWidth: '300px' }}
        />
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <input
          type="date"
          value={checkOutDate}
          onChange={handleCheckOutDateChange}
          style={{ width: '100%', maxWidth: '300px' }}
        />
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
        <Button variant="contained" onClick={handleSubmit}>Submit</Button>
      </Box>
    </Container>
  );
}
