import React from 'react';
import { Box, Button, Typography } from '@mui/material';
import SelectLabelsSingle from './components/SelectBox'; // Booking options
import { WovenImageListDouble } from './components/WovenImageList';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: '100vh',
                backgroundColor: 'white',
                padding: '15px 30px',
            }}
        >
            {/* Header Section */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '10px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '10px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Flexbox layout for Booking Table + Image Gallery */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row',
                    gap: '20px',
                }}
            >
                {/* Left Column: Booking Form */}
                <Box
                    sx={{
                        flex: 2,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'flex-start',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3 style={{ marginBottom: '16px' }}>Book Your Stay</h3>
                    <SelectLabelsSingle />

                    <Box sx={{ marginTop: '20px' }}>
                        <Button
                            variant="contained"
                            color="primary"
                            sx={{
                                backgroundColor: 'red',
                                '&:hover': { backgroundColor: 'darkred' },
                            }}
                        >
                            Confirm Booking
                        </Button>
                    </Box>
                </Box>

                {/* Right Column: Image Gallery */}
                <Box
                    sx={{
                        flex: 1,
                        display: 'flex',
                        flexDirection: 'column',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3 style={{ marginBottom: '16px' }}>Room Images</h3>
                    <WovenImageListDouble />
                </Box>
            </Box>

            {/* Description Section under Booking Table */}
            <Box
                sx={{
                    marginTop: '40px',
                    backgroundColor: 'lightgrey',
                    padding: '20px',
                    borderRadius: '8px',
                    boxShadow: 3,
                }}
            >
                <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
                    About Our Guest House
                </Typography>
                <Typography variant="body1" sx={{ marginTop: '10px' }}>
                    Welcome to E-Just Hotel Guest House, a place designed for comfort and luxury.
                    Whether you're here for business or leisure, we ensure that every stay is
                    exceptional. Our rooms are equipped with modern amenities and offer a relaxing
                    ambiance that makes you feel at home. Explore our image gallery to see the rooms,
                    amenities, and the stunning views we offer. Book now and experience comfort like never before.
                </Typography>
            </Box>
        </Box>
    );
}

export default BookNow;
