import { Box } from '@mui/material';
import React from 'react';
import { SelectLabelsSingle } from './components/selectBox';
import { WovenImageListSingle } from './components/ImageList'; // Adjusted for Single Room
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';

function BookNowSingle() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
            }}
        >
            {/* Header Section */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Select Labels for Room Type */}
            <SelectLabelsSingle />

            {/* Room Description */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Single Room</h3>
                <p>
                    Meals can be prepared in the kitchenette, which has an oven, a microwave, a tea and coffee maker, and a toaster.
                    The spacious room features air conditioning, a minibar, a terrace with a quiet street view as well as a private bathroom boasting a bath.
                    The unit has 1 bed.
                </p>
            </Box>

            {/* Facilities Section */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Facilities of E-Just Hotel Borg El Arab</h3>
                <p>
                    Most popular facilities:
                    <ul style={{ listStyleType: 'none', padding: 0, margin: 0 }}>
                        <li>Room service</li>
                        <li>Free parking</li>
                        <li>Facilities for disabled guests</li>
                        <li>Free Wifi</li>
                        <li>Non-smoking rooms</li>
                        <li>Tea/Coffee Maker in All Rooms</li>
                        <li>Breakfast</li>
                    </ul>
                </p>
            </Box>

            {/* Image Section */}
            <Box
                sx={{
                    position: 'absolute',
                    top: 250,
                    right: 70,
                    width: '40%',
                    height: '90vh',
                    margin: '10px',
                }}
            >
                <Box
                    sx={{
                        width: '50%',
                        height: '100%',
                    }}
                >
                    <WovenImageListSingle /> {/* Show images for the Single Room */}
                </Box>
            </Box>

            {/* Book Activities Button */}
            <Box>
                <Link to="/Bookservices">
                    <Button
                        variant="contained"
                        sx={{
                            position: 'absolute',
                            top: 1000,
                            right: 80,
                            width: '42%',
                            height: '30vh',
                            margin: '0px',
                            backgroundColor: "grey",
                            color: "white",
                            padding: "25px 20px",
                            fontSize: "18px",
                        }}
                    >
                        <h1>Book Your activities/services</h1>
                    </Button>
                </Link>
            </Box>
        </Box>
    );
}

export default BookNowSingle;
