import React, { useState } from "react";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { TextField, Dialog, DialogTitle, DialogContent, DialogActions, List, ListItem, ListItemText, Grid } from "@mui/material";
import { useNavigate } from 'react-router-dom';

export default function SelectLabelsSingle() {
  const [roomType, setRoomType] = useState("");
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const [guests] = useState(1);
  const [error, setError] = useState(false);
  const [totalPrice, setTotalPrice] = useState(0);
  const [open, setOpen] = useState(false);
  const [bookings, setBookings] = useState([]);
  const navigate = useNavigate();

  const handleRoomChange = (event) => {
    setRoomType(event.target.value);
    setError(false);
  };

  const handleCheckInDateChange = (newDate) => {
    setCheckInDate(newDate);
  };

  const handleCheckOutDateChange = (newDate) => {
    setCheckOutDate(newDate);
  };

  const calculateTotalPrice = () => {
    if (checkInDate && checkOutDate) {
      const checkIn = checkInDate.toDate();
      const checkOut = checkOutDate.toDate();
      const timeDifference = checkOut - checkIn;
      const daysBooked = timeDifference / (1000 * 3600 * 24);
      return daysBooked * 400; // Price for single room
    }
    return 0;
  };

  const handleSubmit = async () => {
    if (!roomType || !checkInDate || !checkOutDate) {
      setError(true);
      alert("Please fill in all required fields.");
      return;
    }
    const totalPrice = calculateTotalPrice();
    setTotalPrice(totalPrice);
    alert(`Your total price is ${totalPrice} EGP.`);

    const userId = localStorage.getItem("userId");
    const requestBody = {
      roomType,
      checkInDate: checkInDate.toISOString(),
      checkOutDate: checkOutDate.toISOString(),
      guests,
      userId,
      totalPrice,
    };

    try {
      const response = await fetch("http://localhost:5000/api/booking/details", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Booking was successful.");
        localStorage.setItem("bookingId", data.bookingId);
        navigate('/Bookservices');
      } else {
        alert(data.message || "Could not complete booking.");
      }
    } catch (error) {
      alert(error.message || "An error occurred while booking.");
    }
  };

  const handleShowBookings = async () => {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      alert("You must log in to view bookings.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
      const data = await response.json();

      if (response.ok) {
        setBookings(data.bookings);
        setOpen(true);
      } else {
        alert(data.message || "Could not retrieve bookings.");
      }
    } catch (error) {
      alert(error.message || "An error occurred while retrieving bookings.");
    }
  };

  return (
    <Box sx={{ maxWidth: 500, margin: "0 auto", paddingTop: 3 }}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <FormControl fullWidth error={error}>
            <InputLabel>Room Type</InputLabel>
            <Select value={roomType} label="Room Type" onChange={handleRoomChange}>
              <MenuItem value="Standard Single Room">Standard Single Room</MenuItem>
            </Select>
            {error && <FormHelperText>Room type is required</FormHelperText>}
          </FormControl>
        </Grid>

        <Grid item xs={12} sm={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
              label="Check-in Date"
              value={checkInDate}
              onChange={handleCheckInDateChange}
              renderInput={(props) => <TextField {...props} fullWidth />}
            />
          </LocalizationProvider>
        </Grid>

        <Grid item xs={12} sm={6}>
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DatePicker
              label="Check-out Date"
              value={checkOutDate}
              onChange={handleCheckOutDateChange}
              renderInput={(props) => <TextField {...props} fullWidth />}
            />
          </LocalizationProvider>
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" fullWidth onClick={handleSubmit}>
            Submit
          </Button>
        </Grid>

        <Grid item xs={12}>
          <Button variant="outlined" fullWidth onClick={handleShowBookings}>
            Show Bookings
          </Button>
        </Grid>
      </Grid>

      {/* Dialog for bookings */}
      <Dialog open={open} onClose={() => setOpen(false)}>
        <DialogTitle>Your Bookings</DialogTitle>
        <DialogContent>
          <List>
            {bookings.map((booking, index) => (
              <ListItem key={index}>
                <ListItemText
                  primary={`Room Type: ${booking.roomType}`}
                  secondary={`Check-in: ${booking.checkInDate}, Check-out: ${booking.checkOutDate}, Total Price: ${booking.totalPrice}`}
                />
              </ListItem>
            ))}
          </List>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpen(false)} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
