import { Box } from '@mui/material';
import React from 'react';
import SelectLabelsSingle from './components/selectBox';
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: '100vh',
                backgroundColor: 'white',
                padding: '15px 30px',
                backgroundRepeat: 'no-repeat',
            }}
        >
            {/* Header */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Content Layout */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    gap: '20px',
                }}
            >
                {/* Booking Section */}
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '20px',
                        width: '60%',
                    }}
                >
                    <SelectLabelsSingle />

                    <Box
                        sx={{
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                            minHeight: '200px',
                        }}
                    >
                        <h3>Deluxe Double Room</h3>
                        <p>
                            Meals can be prepared in the kitchenette, which has an oven, a microwave, a tea and coffee
                            maker, and a toaster. The spacious double room features air conditioning, a mini-bar, a
                            terrace with a quiet street view, and a private bathroom boasting a bath.
                        </p>
                    </Box>

                    <Box
                        sx={{
                            boxShadow: 3,
                            padding: '20px',
                            borderRadius: '8px',
                            backgroundColor: 'white',
                            minHeight: '200px',
                        }}
                    >
                        <h3>Facilities</h3>
                        <ul>
                            <li>Safe box</li>
                            <li>Cleaning services</li>
                            <li>Air conditioning</li>
                            <li>Ensuite bathroom</li>
                            <li>Soundproofing</li>
                        </ul>
                    </Box>
                </Box>

                {/* Image Section */}
                <Box
                    sx={{
                        width: '40%',
                        display: 'flex',
                        flexDirection: 'column',
                        gap: '20px',
                        alignItems: 'center',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3>Room Photos</h3>
                    <img
                        src="https://via.placeholder.com/300x200"
                        alt="Room Photo 1"
                        style={{ width: '100%', borderRadius: '8px' }}
                    />
                    <img
                        src="https://via.placeholder.com/300x200"
                        alt="Room Photo 2"
                        style={{ width: '100%', borderRadius: '8px' }}
                    />
                </Box>
            </Box>
        </Box>
    );
}

export default BookNow;
