import { Box, Button } from '@mui/material';
import React, { useState } from 'react';
import MergedSelectComponent from "./components/selectBoxservices";

function Bookservices() {

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                backgroundSize: "cover",
                padding: "15px 30px",
                backgroundRepeat: "no-repeat",
            }}
        >
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just services</h1>
                </div>
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Book your food bundle</h3>
                <MergedSelectComponent
                    type="food"
                    onFoodChange={(value) => setFoodChoice(value)}
                    onCustomPeriodChange={(value) => setCustomPeriod(value)}
                    onMealTypeChange={(value) => setMealType(value)}
                />
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Book your sport court</h3>
                <MergedSelectComponent
                    type="court"
                    onCourtTypeChange={(value) => setCourtChoice(value)}
                    onCourtTimeChange={(value) => setCourtTime(value)}
                />
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Book your gym time</h3>
                <MergedSelectComponent
                    type="gym"
                    onGymTimeChange={(value) => setGymTime(value)}
                />
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    marginTop: '30px',
                }}
            >
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSubmit}
                >
                    Submit All Choices
                </Button>
            </Box>
        </Box>
    );
}

export default Bookservices;
