import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { 
  TextField, 
  Typography, 
  IconButton, 
  Avatar, 
  Tooltip, 
  AppBar, 
  Toolbar, 
  Container, 
  Menu, 
  Box, 
  Button 
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { Link } from 'react-router-dom';

// Your component code goes here


const itemData = [
    { img: '/roomimages/bathroom.jpg', title: 'Bed' },
    { img: '/roomimages/room2.jpg', title: 'Sink' },
    { img: '/roomimages/room3.jpg', title: 'Books' },
    { img: '/roomimages/kitchen.jpg', title: 'Chairs' },
    { img: '/roomimages/kitchen2.jpg', title: 'Candle' },
    { img: '/roomimages/livingroom.jpg', title: 'Laptop' },
    { img: '/roomimages/room4.jpg', title: 'Doors' },
    { img: '/roomimages/livingroom2.jpg', title: 'Coffee' },
    { img: '/roomimages/livingroom3.jpg', title: 'Storage' },
    { img: '/roomimages/building.jpg', title: 'Coffee table' },
    { img: '/roomimages/view.jpg', title: 'Blinds' },
];

const pages = [
    { name: 'Book NOW', link: '/room-Type' },
    { name: 'Facilities', link: '/facilities' },
    { name: 'Contact Us', link: '/about-us' },
];

const settings = [
    { name: 'Profile', link: '/profile' },
    { name: 'Sign In', link: '/signinPage' },
    { name: 'Bookings', link: '/bookings' },
    { name: 'Logout', link: '/logout' },
];

export default function App() {
    const [roomType, setRoomType] = React.useState('');
    const [guests, setGuests] = React.useState('');
    const [error, setError] = React.useState(false);
    const [open, setOpen] = React.useState(false);
    const [selectedImage, setSelectedImage] = React.useState(null);
    const [checkInDate, setCheckInDate] = React.useState(null);
    const [checkOutDate, setCheckOutDate] = React.useState(null);
    const [anchorElNav, setAnchorElNav] = React.useState(null);
    const [anchorElUser, setAnchorElUser] = React.useState(null);

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
        setGuests(''); // Reset number of guests when room type changes
        setError(false); // Reset error message when room type changes
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = async () => {
        // If the room type is "kingDeluxe" and no guests are selected, show error
        if (roomType === 'kingDeluxe' && !guests) {
            setError(true);
        } else {
            setError(false);
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ roomType, guests }),
            });

            const data = await response.json();
            if (response.ok) {
                console.log(`Booking details: Room Type: ${roomType}, Number of Guests: ${guests}`);
            } else {
                alert(data.message || 'Failed to submit the booking details');
            }
        }
    };

    const handleClickOpen = (img) => {
        setSelectedImage(img); // Set the clicked image
        setOpen(true);         // Open the dialog
    };

    const handleClose = () => {
        setOpen(false);        // Close the dialog
    };

    const handleCheckInDateChange = (newDate) => {
        setCheckInDate(newDate);
    };

    const handleCheckOutDateChange = (newDate) => {
        setCheckOutDate(newDate);
    };

    const handleSubmitDates = async () => {
        if (!checkInDate || !checkOutDate) {
            alert("Please select both check-in and check-out dates");
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/booking/dates", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ checkInDate: checkInDate.toISOString(), checkOutDate: checkOutDate.toISOString() }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Error occurred");
            }

            const data = await response.json();
            console.log("Dates saved successfully:", data);
        } catch (error) {
            console.error("Error saving dates:", error.message);
            alert(error.message || "Failed to save dates");
        }
    };

    const handleOpenNavMenu = (event) => {
        setAnchorElNav(event.currentTarget);
    };
    const handleOpenUserMenu = (event) => {
        setAnchorElUser(event.currentTarget);
    };

    const handleCloseNavMenu = () => {
        setAnchorElNav(null);
    };

    const handleCloseUserMenu = () => {
        setAnchorElUser(null);
    };

    return (
        <div>
            {/* AppBar */}
            <AppBar position="static" sx={{ backgroundColor: 'black' }}>
                <Container maxWidth="xl">
                    <Toolbar disableGutters>
                        <Box sx={{ display: 'flex', alignItems: 'center', mr: 2 }}>
                            <img
                                src="/logoBlack.png"
                                alt="E-JUST Logo"
                                style={{ padding: '20px 50px', width: '400px', height: '80px', marginRight: '40px' }}
                            />
                            <Typography
                                fontStyle="initial"
                                variant="h6"
                                noWrap
                                component="a"
                                href="#"
                                sx={{
                                    fontFamily: 'monospace',
                                    fontWeight: 700,
                                    color: 'white',
                                    textDecoration: 'none',
                                }}
                            >
                                E-JUST Accommodations
                            </Typography>
                        </Box>
                        <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
                            <IconButton
                                size="large"
                                aria-label="open navigation menu"
                                aria-controls="menu-appbar"
                                aria-haspopup="true"
                                onClick={handleOpenNavMenu}
                                color="inherit"
                            >
                                <MenuIcon />
                            </IconButton>
                            <Menu
                                id="menu-appbar"
                                anchorEl={anchorElNav}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                                keepMounted
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'left',
                                }}
                                open={Boolean(anchorElNav)}
                                onClose={handleCloseNavMenu}
                                sx={{ display: { xs: 'block', md: 'none' } }}
                            >
                                {pages.map((page) => (
                                    <MenuItem key={page.name} onClick={handleCloseNavMenu}>
                                        <Typography textAlign="center">
                                            <Link to={page.link} style={{ textDecoration: 'none', color: 'grey' }}>
                                                {page.name}
                                            </Link>
                                        </Typography>
                                    </MenuItem>
                                ))}
                            </Menu>
                        </Box>
                        <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
                            {pages.map((page) => (
                                <Button
                                    key={page.name}
                                    component={Link}
                                    to={page.link}
                                    sx={{ my: 2, color: 'white', display: 'block', textDecoration: 'none' }}
                                >
                                    {page.name}
                                </Button>
                            ))}
                        </Box>
                        <Box sx={{ flexGrow: 0 }}>
                            <Tooltip title="Open settings">
                                <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                                    <Avatar alt="User Avatar" src="/static/images/avatar/2.jpg" />
                                </IconButton>
                            </Tooltip>
                            <Menu
                                sx={{ mt: '45px' }}
                                id="menu-appbar"
                                anchorEl={anchorElUser}
                                anchorOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right',
                                }}
                                keepMounted
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right',
                                }}
                                open={Boolean(anchorElUser)}
                                onClose={handleCloseUserMenu}
                            >
                                {settings.map((setting) => (
                                    <MenuItem key={setting.name} onClick={handleCloseUserMenu}>
                                        <Typography textAlign="center">
                                            <Link to={setting.link} style={{ textDecoration: 'none', color: 'grey' }}>
                                                {setting.name}
                                            </Link>
                                        </Typography>
                                    </MenuItem>
                                ))}
                            </Menu>
                        </Box>
                    </Toolbar>
                </Container>
            </AppBar>

            <main>
                <Box sx={{ flexGrow: 1, py: 10 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'center' }}>
                        <FormControl fullWidth error={error} sx={{ maxWidth: 300 }}>
                            <InputLabel>Room Type</InputLabel>
                            <Select value={roomType} onChange={handleRoomChange} label="Room Type">
                                <MenuItem value="kingDeluxe">King Deluxe</MenuItem>
                                <MenuItem value="queenStandard">Queen Standard</MenuItem>
                            </Select>
                            {error && <FormHelperText>Guests must be selected</FormHelperText>}
                        </FormControl>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
                        <FormControl fullWidth sx={{ maxWidth: 300 }}>
                            <InputLabel>Number of Guests</InputLabel>
                            <Select value={guests} onChange={handleGuestsChange} label="Number of Guests">
                                <MenuItem value={1}>1</MenuItem>
                                <MenuItem value={2}>2</MenuItem>
                            </Select>
                        </FormControl>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
                        <Button variant="contained" onClick={handleSubmit}>
                            Submit
                        </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                        <Button variant="contained" onClick={handleSubmitDates}>
                            Submit Dates
                        </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                        <ImageList sx={{ width: 500, height: 450 }} cols={3} rowHeight={164}>
                            {itemData.map((item) => (
                                <ImageListItem key={item.img}>
                                    <img
                                        src={`${item.img}?w=164&h=164&fit=crop&auto=format`}
                                        alt={item.title}
                                        loading="lazy"
                                        onClick={() => handleClickOpen(item.img)}
                                    />
                                </ImageListItem>
                            ))}
                        </ImageList>
                    </Box>
                </Box>
            </main>

            <Dialog open={open} onClose={handleClose}>
                <DialogContent>
                    <img src={selectedImage} alt="Selected" style={{ width: '100%' }} />
                </DialogContent>
            </Dialog>
        </div>
    );
}
