import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Paper, List, ListItem, ListItemText, CircularProgress } from "@mui/material";
import { useNavigate } from "react-router-dom";

const ConfirmationPage = () => {
  const [bookingDetails, setBookingDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBookingDetails = async () => {
      const userId = localStorage.getItem("userId");
      if (!userId) {
        alert("User not logged in");
        return;
      }

      try {
        const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
        const data = await response.json();

        if (response.ok) {
          const latestBooking = data.bookings[data.bookings.length - 1]; // Get the latest booking
          setBookingDetails(latestBooking);
        } else {
          setError("Failed to fetch booking details");
        }
      } catch (err) {
        setError("Error fetching booking details");
      } finally {
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6" color="error">
          {error}
        </Typography>
      </Box>
    );
  }

  if (!bookingDetails) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6">No booking found.</Typography>
      </Box>
    );
    // Assuming you're using an Express.js handler for this route

const getServicesByBookingId = async (req, res) => {
  const { bookingId } = req.params;  // Extract the bookingId from request params

  try {
    // First, retrieve the booking by the bookingId
    const booking = await Booking.findOne({ _id: bookingId });

    if (!booking) {
      return res.status(404).json({ error: 'Booking not found' });
    }

    // Retrieve services based on the bookingId
    const services = await Service.find({ bookingId: bookingId }); 

    // If services are found, send them in the response
    if (services.length === 0) {
      return res.status(404).json({ error: 'No services found for this booking' });
    }

    // Return the services as a response
    return res.status(200).json({ services });
  } catch (error) {
    console.error(error);
    return res.status(500).json({ error: 'Server error' });
  }
};
 }

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: "url('./background2.jpg')", // Background image
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        padding: 4,
      }}
    >
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={8} md={6}>
          <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
              Booking Confirmation
            </Typography>

            <Typography variant="h6" color="primary" gutterBottom>
              Booking Details
            </Typography>
            <List>
              <ListItem>
                <ListItemText primary="Room Type" secondary={bookingDetails.roomType} />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Check-in Date"
                  secondary={new Date(bookingDetails.checkInDate).toLocaleDateString()}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Check-out Date"
                  secondary={new Date(bookingDetails.checkOutDate).toLocaleDateString()}
                />
              </ListItem>
              <ListItem>
                <ListItemText primary="Number of Guests" secondary={bookingDetails.guests} />
              </ListItem>
            </List>

            <Typography variant="h6" color="primary" gutterBottom sx={{ marginTop: 3 }}>
              Services Booked
            </Typography>
            <List>
              {bookingDetails.services?.length ? (
                bookingDetails.services.map((service, index) => (
                  <ListItem key={index}>
                    <ListItemText primary={service.name} secondary={`Price: ${service.price}`} />
                  </ListItem>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="No services booked" />
                </ListItem>
              )}
            </List>

            <Box sx={{ display: "flex", justifyContent: "center", marginTop: 4 }}>
              <Button variant="contained" color="primary" onClick={() => navigate("/Book-Now")}>
                Book Another Room
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ConfirmationPage;
