import React, { useState } from 'react';
import {
    Box,
    Button,
    FormControl,
    InputLabel,
    MenuItem,
    Select,
    TextField,
} from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';

function CombinedComponent() {
    const [roomType, setRoomType] = useState('');
    const [guests, setGuests] = useState('');
    const [checkInDate, setCheckInDate] = useState(null);
    const [checkOutDate, setCheckOutDate] = useState(null);
    const [error, setError] = useState(false);

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
        setGuests('');
        setError(false);
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = () => {
        if (roomType === 'kingDeluxe' && !guests) {
            setError(true);
        } else {
            setError(false);
            console.log({
                roomType,
                guests,
                checkInDate: checkInDate?.toString(),
                checkOutDate: checkOutDate?.toString(),
            });
        }
    };

    return (
        <Box>
            {/* Room Type Selector */}
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    onChange={handleRoomChange}
                >
                    <MenuItem value="">
                        <em>None</em>
                    </MenuItem>
                    <MenuItem value="kingDeluxe">King Deluxe Room</MenuItem>
                    <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
                </Select>
            </FormControl>

            {/* Guests Selector */}
            {roomType === 'kingDeluxe' && (
                <FormControl sx={{ m: 1, minWidth: 400 }} error={error}>
                    <InputLabel id="guests-label">Number of Guests</InputLabel>
                    <Select
                        labelId="guests-label"
                        id="guests-select"
                        value={guests}
                        onChange={handleGuestsChange}
                    >
                        <MenuItem value="">
                            <em>None</em>
                        </MenuItem>
                        <MenuItem value={1}>1 Guest</MenuItem>
                        <MenuItem value={2}>2 Guests</MenuItem>
                    </Select>
                </FormControl>
            )}

            {/* Date Pickers */}
            <LocalizationProvider dateAdapter={AdapterDayjs}>
                <Box
                    sx={{
                        display: 'flex',
                        flexDirection: 'column',
                        gap: 2,
                        marginTop: '20px',
                    }}
                >
                    <DatePicker
                        label="Check-in Date"
                        value={checkInDate}
                        onChange={(newDate) => setCheckInDate(newDate)}
                        renderInput={(params) => <TextField {...params} />}
                    />
                    <DatePicker
                        label="Check-out Date"
                        value={checkOutDate}
                        onChange={(newDate) => setCheckOutDate(newDate)}
                        renderInput={(params) => <TextField {...params} />}
                    />
                </Box>
            </LocalizationProvider>

            {/* Submit Button */}
            <Button
                onClick={handleSubmit}
                variant="contained"
                color="primary"
                sx={{ marginTop: '20px' }}
            >
                Submit
            </Button>
        </Box>
    );
}

export default CombinedComponent;
