import { Box } from '@mui/material';
import React from 'react';
import SelectLabelsSingle from './components/selectBox'; // Updated to Single

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
                display: 'flex', // Use flex to control layout
                flexDirection: 'row', // Set items side by side
            }}
        >
            {/* Left Side: Booking Table */}
            <Box
                sx={{
                    flex: 1,
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                }}
            >
                <Box
                    sx={{
                        marginBottom: '20px',
                        backgroundColor: 'red',
                        color: 'white',
                        padding: '1px',
                        borderRadius: '8px',
                        width: '100%',
                        textAlign: 'center',
                    }}
                >
                    <h1>E-Just Hotel Guest House</h1>
                </Box>

                <SelectLabelsSingle />

                {/* Room Details */}
                <Box
                    sx={{
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                        width: '100%',
                        maxWidth: '600px',
                        marginTop: '20px',
                        minHeight: '200px',
                    }}
                >
                    <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>Deluxe Double Room</h3>
                    <p style={{ lineHeight: '1.6', textAlign: 'left', marginBottom: '16px' }}>
                        Meals can be prepared in the kitchenette, which has an oven, a microwave, a tea and coffee maker,
                        and a toaster. The spacious double room features air conditioning, a mini-bar, a terrace with a
                        quiet street view, and a private bathroom boasting a bath.
                    </p>
                </Box>

                {/* Facilities */}
                <Box
                    sx={{
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                        width: '100%',
                        maxWidth: '600px',
                        marginTop: '20px',
                        minHeight: '200px',
                    }}
                >
                    <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>Facilities</h3>
                    <ul style={{ textAlign: 'left' }}>
                        <li>Safe box</li>
                        <li>Cleaning services</li>
                        <li>Air conditioning</li>
                        <li>Ensuite bathroom</li>
                        <li>Soundproofing</li>
                    </ul>
                </Box>
            </Box>

            {/* Right Side: Photos */}
            <Box
                sx={{
                    flex: 1,
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    marginLeft: '20px', // Space between booking table and photos
                }}
            >
                <Box
                    sx={{
                        width: '80%',
                        height: 'auto',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    {/* Replace with your ImageList component or images */}
                    <h3 style={{ textAlign: 'center', marginBottom: '16px' }}>Room Photos</h3>
                    {/* Example: */}
                    <img
                        src="https://via.placeholder.com/400x300"
                        alt="Deluxe Room"
                        style={{
                            width: '100%',
                            height: 'auto',
                            borderRadius: '8px',
                        }}
                    />
                </Box>
            </Box>
        </Box>
    );
}

export default BookNow;
