import React, { useEffect, useState } from "react";

function ConfirmationPage() {
  const [bookingDetails, setBookingDetails] = useState(null);
  const [servicesDetails, setServicesDetails] = useState(null);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    const bookingId = localStorage.getItem("bookingId");

    // Fetch room booking details
    fetch(`http://localhost:5000/api/user/:${userId}`) // Adjusted endpoint
      .then((response) => response.json())
      .then((data) => {
        const booking = data.bookings.find((b) => b._id === bookingId); // Match booking by ID
        setBookingDetails(booking);
      })
      .catch((error) => console.error("Error fetching booking details:", error));

    // Fetch services booked
    fetch(`http://localhost:5000/api/:${bookingId}`) // Adjusted endpoint
      .then((response) => response.json())
      .then((data) => {
        setServicesDetails(data.booking); // Access the service booking data
      })
      .catch((error) => console.error("Error fetching service details:", error));
  }, []);

  if (!bookingDetails || !servicesDetails) {
    return (
      <div style={{ fontFamily: "Arial, sans-serif", textAlign: "center", padding: "20px" }}>
        <h1>Loading...</h1>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "800px", margin: "0 auto" }}>
      <h1 style={{ textAlign: "center", color: "#007BFF" }}>Booking Confirmation</h1>

      <div style={{ border: "1px solid #ddd", padding: "20px", borderRadius: "5px", marginBottom: "20px" }}>
        <h2 style={{ borderBottom: "2px solid #007BFF", paddingBottom: "10px" }}>Room Details</h2>
        <p>
          <strong>Room Type:</strong> {bookingDetails.roomType}
        </p>
        <p>
          <strong>Number of Guests:</strong> {bookingDetails.guests}
        </p>
        <p>
          <strong>Check-in Date:</strong> {new Date(bookingDetails.checkInDate).toLocaleDateString()}
        </p>
        <p>
          <strong>Check-out Date:</strong> {new Date(bookingDetails.checkOutDate).toLocaleDateString()}
        </p>
      </div>

      <div style={{ border: "1px solid #ddd", padding: "20px", borderRadius: "5px" }}>
        <h2 style={{ borderBottom: "2px solid #007BFF", paddingBottom: "10px" }}>Services Booked</h2>
        <ul style={{ listStyleType: "none", padding: "0" }}>
          <li style={{ marginBottom: "10px" }}>
            <strong>Food:</strong> {servicesDetails.food ? "Booked" : "Not Booked"}
          </li>
          <li style={{ marginBottom: "10px" }}>
            <strong>Court:</strong> {servicesDetails.court ? "Booked" : "Not Booked"}
          </li>
          <li style={{ marginBottom: "10px" }}>
            <strong>Gym:</strong> {servicesDetails.gym ? "Booked" : "Not Booked"}
          </li>
        </ul>
      </div>
    </div>
  );
}

export default ConfirmationPage;
