const handleCancelBooking = async (bookingId) => {
    console.log("Booking ID passed to handleCancelBooking:", bookingId);

    if (!bookingId) {
        alert("Invalid booking ID.");
        return;
    }

    try {
        const response = await fetch(
            `http://localhost:5000/api/bookings/${bookingId}`,
            { method: "DELETE" }
        );

        if (response.ok) {
            setBookings((prevBookings) =>
                prevBookings.filter((booking) => booking._id !== bookingId)
            );
            alert("Booking canceled successfully.");
        } else {
            const data = await response.json();
            alert(data.message || "Failed to cancel booking. Please try again.");
        }
    } catch (err) {
        console.error("Error canceling booking:", err);
        alert("Error connecting to the server. Please try again later.");
    }
};

<IconButton
    edge="end"
    aria-label="delete"
    onClick={() => {
        console.log(`Deleting booking with ID: ${booking._id}`);
        handleCancelBooking(booking._id); // Pass booking._id here
    }}
>
    <DeleteIcon />
</IconButton>
