import React from 'react';
import { Box, Button } from '@mui/material';
import SelectLabelsSingle from './components/SelectBox'; // Booking options
import { WovenImageListDouble } from './components/ImageListDoubler';

function BookNow() {
    return (
        <Box
            sx={{
                minHeight: '100vh',
                backgroundColor: 'white',
                padding: '15px 30px',
            }}
        >
            {/* Header Section */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '10px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '10px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Flexbox layout for Booking Table + Image Gallery */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'row',
                    gap: '20px',
                }}
            >
                {/* Left Column: Booking Form + Description */}
                <Box
                    sx={{
                        flex: 2,
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'flex-start',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3 style={{ marginBottom: '16px' }}>Book Your Stay</h3>
                    <p style={{ marginBottom: '16px' }}>
                        Welcome to E-Just Hotel Guest House. Use the form below to book your stay
                        in our luxury rooms, fully equipped with modern amenities.
                    </p>

                    {/* Booking Options */}
                    <SelectLabelsSingle />

                    <Box sx={{ marginTop: '20px' }}>
                        <Button
                            variant="contained"
                            color="primary"
                            sx={{
                                backgroundColor: 'red',
                                '&:hover': { backgroundColor: 'darkred' },
                            }}
                        >
                            Confirm Booking
                        </Button>
                    </Box>
                </Box>

                {/* Right Column: Image Gallery + Additional Info */}
                <Box
                    sx={{
                        flex: 1,
                        display: 'flex',
                        flexDirection: 'column',
                        boxShadow: 3,
                        padding: '20px',
                        borderRadius: '8px',
                        backgroundColor: 'white',
                    }}
                >
                    <h3 style={{ marginBottom: '16px' }}>Room Images</h3>
                    <p style={{ marginBottom: '16px' }}>
                        Browse our image gallery to preview our luxurious rooms and amenities.
                    </p>

                    {/* Image Gallery */}
                    <WovenImageListDouble />
                </Box>
            </Box>
        </Box>
    );
}

export default BookNow;
