import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';

export default function SelectLabelsDouble() {
    const [guests, setGuests] = React.useState('');

    const handleChange = (event) => {
        setGuests(event.target.value);
    const handleSubmit = async () => {
            const bookingId = 'your-booking-id'; // Replace with actual bookingId from your backend
            try {
                const response = await axios.post('/booking/details', {
                    bookingId,
                    roomType: 'deluxeDouble', // You can modify it as per the requirements
                    guests
                });
                console.log(response.data);
            } catch (error) {
                console.error('Error submitting details:', error);
            }
        };
    
    };

    return (
        <Box

        >
            <FormControl sx={{ m: 1, minWidth: 400, justifyContent: 'center', }}>
                <InputLabel id="demo-simple-select-helper-label">Number of Guests</InputLabel>
                <Select
                    labelId="demo-simple-select-helper-label"
                    id="demo-simple-select-helper"
                    value={guests}
                    label="Double room type"
                    onChange={handleChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value={1}>Deluxe Double Room</MenuItem>

                </Select>
                <FormHelperText>select room Type</FormHelperText>
            </FormControl>
        </Box>
    );
}
