import { Box } from '@mui/material';
import React from 'react';
import SelectLabels from "./components/selectBox";
import CustomSlotPropsCallback from "./components/dateSelect";
import WovenImageList from "./components/ImageList";
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';

function BookNow() {
    
        const [roomType, setRoomType] = useState('');
        const [guests, setGuests] = useState('');
        const navigate = useNavigate();
    
        const handleRoomChange = (event) => {
            setRoomType(event.target.value);
        };
    
        const handleGuestsChange = (event) => {
            setGuests(event.target.value);
        };
    
        const handleSubmit = () => {
            if (roomType && guests) {
                // Navigate to Booking.js and pass the selected data
                navigate('/booking', { state: { roomType, guests } });
            } else {
                alert("Please select both room type and number of guests");
            }
        };
    return (
        <Box
            sx={{
                minHeight: "100vh", // Full viewport height
                //backgroundImage: "url('./download.jpg')", // Path to your image in the public folder
                backgroundColor: "white",
                backgroundSize: "cover", // Ensures the image covers the whole area
                padding: "15px 30px",
                backgroundRepeat: "no-repeat", // Prevents repeating
            }}
        >
            {/* Header Section */}
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just Hotel Guest House</h1>
                </div>
            </Box>

            {/* Select Labels */}
            <SelectLabels />

            {/* Date Selector */}
            <CustomSlotPropsCallback />

            {/*discription boarder */}
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',   // Align to the left
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',              // Allow full width
                    maxWidth: '600px',          // Limit the width to 600px
                    marginLeft: '10px',
                    marginTop: '20px',
                    height: 'auto',             // Adjust height based on content
                    minHeight: '200px',         // Optional: Add a minimum height for consistency
                    '@media (max-width:600px)': {
                        width: '90%',           // Adjust width for smaller screens
                        padding: '16px',        // Adjust padding for smaller screens
                    }
                }}
            >
                <h3 style={{ textAlign: 'left', marginBottom: '16px' }}>Deluxe Double Room </h3>
                <p style={{ lineHeight: '1.6', textAlign: 'left', marginBottom: '16px' }}>
                    Meals can be prepared in the kitchenette, which has an oven, a microwave, a tea and coffee maker and a toaster. The spacious double room features air conditioning, a mini-bar, a terrace with a quiet street view as well as a private bathroom boasting a bath. The unit has 1 bed.
                </p>
            </Box>
            {/*facilities boarder*/}

            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',   // Align to the left
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',              // Allow full width
                    maxWidth: '600px',          // Limit the width to 600px
                    marginLeft: '20px',
                    marginTop: '20px',
                    height: 'auto',             // Adjust height based on content
                    minHeight: '200px',         // Optional: Add a minimum height for consistency
                    '@media (max-width:600px)': {
                        width: '90%',           // Adjust width for smaller screens
                        padding: '16px',        // Adjust padding for smaller screens
                    }
                }}
            >
                <h3 style={{ textAlign: 'left', marginBottom: '20px' }}>Facilities of E-Just Hotel Borg El Arab</h3>
                <p style={{ lineHeight: '3', textAlign: 'left', marginBottom: '20px' }}>
                    Most popular facilities
                    <ul style={{ listStyleType: 'none', padding: 0, margin: 0 }}>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/room_service_g.png" alt="room_service Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Room service
                        </li>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/local_parking.png" alt="Free parking Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Free parking
                        </li>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/assist_walker_24dp_48752C.png" alt="Disabled facilities Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Facilities for disabled guests
                        </li>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/wifi.png" alt="Free Wifi Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Free Wifi
                        </li>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/smoke_free_24dp_48752C.png" alt="Non-smoking rooms Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Non-smoking rooms
                        </li>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/coffee_maker_24dp_48752C.png" alt="Tea/Coffee Maker Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Tea/Coffee Maker in All Rooms
                        </li>
                        <li style={{ display: 'flex', alignItems: 'center' }}>
                            <img src="/icons/free_breakfast_24dp_48752C.png" alt="Breakfast Logo" style={{ marginRight: '10px', width: '35px', height: '35px' }} />
                            Breakfast
                        </li>
                    </ul>
                </p>

            </Box>
            {/* Image Section */}
            <Box
                sx={{
                    position: 'absolute',       // Absolute positioning
                    top: 280,                     // Align to the top
                    right: 0,                   // Align to the right
                    width: '42%',               // Set desired width
                    height: '90vh',             // Set desired height
                    margin: '10px',             // Optional margin
                }}
            >
                <Box
                    sx={{
                        width: '50%',          // Expands the size of the image list
                        height: '100%',        // Ensures it fills the height of the parent
                    }}
                >
                    <WovenImageList />
                </Box>
            </Box>
            {/*Link button to facilities booking */}
            <Box>
                <Link to="/Bookservices">
                    <Button
                        variant="contained"
                        sx={{
                            position: 'absolute',       // Absolute positioning
                            top: 790,                     // Align to the top
                            right: 80,                   // Align to the right
                            width: '42%',               // Set desired width
                            height: '30vh',             // Set desired height
                            margin: '0px',             // Optional margin
                            backgroundColor: "grey",
                            color: "white",
                            padding: "25px 20px",
                            fontSize: "18px",
                            px: "15px",
                        }}
                    >
                        <h1>Book Your activities/services </h1>
                    </Button>
                </Link>
            </Box>
        </Box>
    );
}

export default BookNow;
