import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';

export default function SelectLabelsDouble() {
    const [guests, setGuests] = React.useState('');

    const handleChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = async () => {
        if (!guests) {
            alert("Please select the number of guests.");
            return;
        }
        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ guests }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Unknown error occurred");
            }

            const data = await response.json();
            console.log("Guest details updated:", data);
        } catch (error) {
            console.error("Error during guest details update:", error.message);
            alert(error.message || "Error updating guest details.");
        }
    };

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="guests-label">Number of Guests</InputLabel>
                <Select
                    labelId="guests-label"
                    id="guests-select"
                    value={guests}
                    label="Number of Guests"
                    onChange={handleChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value={1}>1 Guest</MenuItem>
                    <MenuItem value={2}>2 Guests</MenuItem>
                </Select>
                <FormHelperText>Choose the number of guests</FormHelperText>
            </FormControl>

            <button onClick={handleSubmit}>Submit</button>
        </Box>
    );
}
