import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Paper, List, ListItem, ListItemText, CircularProgress } from "@mui/material";
import { useNavigate } from "react-router-dom";

const ConfirmationPage = () => {
    const [bookingDetails, setBookingDetails] = useState(null);
    const [services, setServices] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchBookingDetails = async () => {
            const userId = localStorage.getItem("userId");
            const bookingId = localStorage.getItem("bookingId");

            // Validate presence of required data
            if (!userId || !bookingId) {
                alert("User or booking ID not found");
                setError("Missing user or booking ID");
                setLoading(false);
                return;
            }

            try {
                // Fetch the booking data
                const bookingResponse = await fetch(`http://localhost:5000/api/booking/${userId}`);
                const bookingData = await bookingResponse.json();

                // Check if the booking data response is valid
                if (!bookingResponse.ok) {
                    throw new Error("Failed to fetch booking details");
                }

                // Find the booking by ID
                const latestBooking = bookingData.bookings.find((booking) => booking._id === bookingId);
                if (!latestBooking) {
                    throw new Error("Booking not found");
                }

                setBookingDetails(latestBooking);

                // Fetch services data associated with the booking
                const servicesResponse = await fetch(`http://localhost:5000/api/services/${bookingId}`);
                const servicesData = await servicesResponse.json();

                if (!servicesResponse.ok) {
                    throw new Error("Failed to fetch services");
                }

                setServices(servicesData.services || []);

            } catch (err) {
                // Set error state in case of failure
                setError(err.message || "Error fetching data");
            } finally {
                setLoading(false);
            }
        };

        fetchBookingDetails();
    }, []);

    // Loading state
    if (loading) {
        return (
            <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
                <CircularProgress />
            </Box>
        );
    }

    // Error state
    if (error) {
        return (
            <Box sx={{ textAlign: "center", marginTop: 3 }}>
                <Typography variant="h6" color="error">
                    {error}
                </Typography>
            </Box>
        );
    }

    // No booking found
    if (!bookingDetails) {
        return (
            <Box sx={{ textAlign: "center", marginTop: 3 }}>
                <Typography variant="h6">No booking found.</Typography>
            </Box>
        );
    }

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundImage: "url('./background2.jpg')",
                backgroundSize: "cover",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                padding: 4,
            }}
        >
            <Grid container spacing={2} justifyContent="center">
                <Grid item xs={12} sm={8} md={6}>
                    <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
                        <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
                            Booking Confirmation
                        </Typography>

                        <Typography variant="h6" color="primary" gutterBottom>
                            Booking Details
                        </Typography>
                        <List>
                            <ListItem>
                                <ListItemText primary="Room Type" secondary={bookingDetails.roomType} />
                            </ListItem>
                            <ListItem>
                                <ListItemText
                                    primary="Check-in Date"
                                    secondary={new Date(bookingDetails.checkInDate).toLocaleDateString()}
                                />
                            </ListItem>
                            <ListItem>
                                <ListItemText
                                    primary="Check-out Date"
                                    secondary={new Date(bookingDetails.checkOutDate).toLocaleDateString()}
                                />
                            </ListItem>
                            <ListItem>
                                <ListItemText primary="Number of Guests" secondary={bookingDetails.guests} />
                            </ListItem>
                        </List>

                        <Typography variant="h6" color="primary" gutterBottom sx={{ marginTop: 3 }}>
                            Services Booked
                        </Typography>
                        <List>
                            {services.length ? (
                                services.map((service, index) => (
                                    <ListItem key={index}>
                                        <ListItemText
                                            primary={service.name || "Unnamed Service"}
                                            secondary={`Price: ${service.price ? `$${service.price}` : "N/A"}`}
                                        />
                                    </ListItem>
                                ))
                            ) : (
                                <ListItem>
                                    <ListItemText primary="No services booked" />
                                </ListItem>
                            )}
                        </List>

                        <Box sx={{ display: "flex", justifyContent: "center", marginTop: 4 }}>
                            <Button variant="contained" color="primary" onClick={() => navigate("/Book-Now")}>
                                Book Another Room
                            </Button>
                        </Box>
                    </Paper>
                </Grid>
            </Grid>
        </Box>
    );
};

export default ConfirmationPage;
