import React, { useEffect, useState } from "react";

function ConfirmationPage() {
  const [bookingDetails, setBookingDetails] = useState(null);
  const [servicesDetails, setServicesDetails] = useState([]);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    const bookingId = localStorage.getItem("bookingId");

    // Fetch room booking details
    fetch(`http://localhost:5000/booking/:userId/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        const booking = data.find((b) => b.bookingId === bookingId);
        setBookingDetails(booking);
      })
      .catch((error) => console.error("Error fetching booking details:", error));

    // Fetch services booked
    fetch(`http://localhost:5000/services/${bookingId}`)
      .then((response) => response.json())
      .then((data) => {
        setServicesDetails(data);
      })
      .catch((error) => console.error("Error fetching service details:", error));
  }, []);

  if (!bookingDetails || !servicesDetails.length) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <h1>Booking Confirmation</h1>

      <h2>Room Details</h2>
      <p>
        <strong>Room Type:</strong> {bookingDetails.roomType}
      </p>
      <p>
        <strong>Number of Guests:</strong> {bookingDetails.guests}
      </p>
      <p>
        <strong>Check-in Date:</strong> {bookingDetails.checkInDate}
      </p>
      <p>
        <strong>Check-out Date:</strong> {bookingDetails.checkOutDate}
      </p>

      <h2>Services Booked</h2>
      <ul>
        {servicesDetails.map((service, index) => (
          <li key={index}>
            <strong>Service:</strong> {service.serviceType} <br />
            <strong>Details:</strong> {service.details} <br />
            <strong>Price:</strong> {service.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ConfirmationPage;
