import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// BookingDetails Class
class BookingDetails {
    constructor(roomType, checkInDate, checkOutDate, guests, userId) {
        this.roomType = roomType;
        this.checkInDate = new Date(checkInDate);
        this.checkOutDate = new Date(checkOutDate);
        this.guests = guests;
        this.userId = userId;
    }

    isValid() {
        if (!this.roomType || !this.checkInDate || !this.checkOutDate || !this.guests) {
            return { valid: false, message: "Please complete all fields" };
        }
        if (this.checkOutDate <= this.checkInDate) {
            return { valid: false, message: "Check-out date must be after check-in date" };
        }
        return { valid: true };
    }

    toRequestBody() {
        return {
            roomType: this.roomType,
            checkInDate: this.checkInDate.toISOString(),
            checkOutDate: this.checkOutDate.toISOString(),
            guests: this.guests,
            userId: this.userId,
        };
    }
}

// SelectLabelsSingle Component
const SelectLabelsSingle = () => {
    const [roomType, setRoomType] = useState('');
    const [checkInDate, setCheckInDate] = useState('');
    const [checkOutDate, setCheckOutDate] = useState('');
    const [guests, setGuests] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async () => {
        const userId = localStorage.getItem("userId");
        const booking = new BookingDetails(roomType, checkInDate, checkOutDate, guests, userId);

        const validation = booking.isValid();
        if (!validation.valid) {
            alert(validation.message);
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(booking.toRequestBody()),
            });

            const data = await response.json();
            if (response.ok) {
                alert("Booking details submitted successfully!");
                navigate('/Bookservices');
            } else {
                alert(data.message || "Failed to submit booking details");
            }
        } catch (error) {
            console.error("Error:", error.message);
            alert("Failed to submit booking details");
        }
    };

    return (
        <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
            <form>
                <div>
                    <label>Room Type:</label>
                    <input
                        type="text"
                        placeholder="Room Type"
                        value={roomType}
                        onChange={(e) => setRoomType(e.target.value)}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    />
                </div>
                <div>
                    <label>Check-In Date:</label>
                    <input
                        type="date"
                        value={checkInDate}
                        onChange={(e) => setCheckInDate(e.target.value)}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    />
                </div>
                <div>
                    <label>Check-Out Date:</label>
                    <input
                        type="date"
                        value={checkOutDate}
                        onChange={(e) => setCheckOutDate(e.target.value)}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    />
                </div>
                <div>
                    <label>Guests:</label>
                    <input
                        type="number"
                        placeholder="Number of Guests"
                        value={guests}
                        onChange={(e) => setGuests(e.target.value)}
                        style={{ width: "100%", marginBottom: "10px", padding: "8px" }}
                    />
                </div>
                <button
                    type="button"
                    onClick={handleSubmit}
                    style={{
                        padding: "10px 20px",
                        backgroundColor: "#007BFF",
                        color: "#fff",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                    }}
                >
                    Submit
                </button>
            </form>
        </div>
    );
};

export default SelectLabelsSingle;
