import * as React from 'react';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import {
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Button,
  TextField,
} from '@mui/material';

export default function BookingForm() {
  const [roomType, setRoomType] = React.useState('');
  const [guests, setGuests] = React.useState('');
  const [checkInDate, setCheckInDate] = React.useState(null);
  const [checkOutDate, setCheckOutDate] = React.useState(null);
  const [error, setError] = React.useState(false);

  // Handle changes for each input field
  const handleRoomChange = (event) => {
    setRoomType(event.target.value);
    setGuests(''); // Reset number of guests when room type changes
    setError(false); // Reset error message
  };

  const handleGuestsChange = (event) => {
    setGuests(event.target.value);
  };

  const handleCheckInDateChange = (newDate) => {
    setCheckInDate(newDate);
  };

  const handleCheckOutDateChange = (newDate) => {
    setCheckOutDate(newDate);
  };

  // Handle form submission
  const handleSubmit = () => {
    // Perform validation
    if (roomType === 'kingDeluxe' && !guests) {
      setError(true);
      return;
    }

    // Create payload
    const payload = {
      roomType,
      guests,
      checkInDate: checkInDate?.toISOString(),
      checkOutDate: checkOutDate?.toISOString(),
    };

    // Post data to the backend
    console.log(payload); // For debugging
    fetch('/api/booking', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload),
    })
      .then((response) => {
        if (response.ok) {
          alert('Booking successful!');
        } else {
          alert('Failed to book.');
        }
      })
      .catch((error) => {
        console.error('Error submitting booking:', error);
      });
  };

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          gap: 3,
          alignItems: 'center',
          padding: 4,
          backgroundColor: 'white',
          boxShadow: 3,
          borderRadius: 2,
          maxWidth: 600,
          margin: '0 auto',
        }}
      >
        {/* Room Type Selector */}
        <FormControl sx={{ width: '100%' }} error={error && roomType === 'kingDeluxe' && !guests}>
          <InputLabel id="room-type-label">Room Type</InputLabel>
          <Select
            labelId="room-type-label"
            id="room-type-select"
            value={roomType}
            onChange={handleRoomChange}
            label="Room Type"
          >
            <MenuItem value="">
              <em>Select a Room Type</em>
            </MenuItem>
            <MenuItem value="kingDeluxe">King Deluxe Room</MenuItem>
            <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
          </Select>
          <FormHelperText>Choose your room type</FormHelperText>
        </FormControl>

        {/* Guests Selector (Only for King Deluxe Room) */}
        {roomType === 'kingDeluxe' && (
          <FormControl sx={{ width: '100%' }} error={error}>
            <InputLabel id="guests-label">Number of Guests</InputLabel>
            <Select
              labelId="guests-label"
              id="guests-select"
              value={guests}
              onChange={handleGuestsChange}
              label="Number of Guests"
            >
              <MenuItem value="">
                <em>Select Number of Guests</em>
              </MenuItem>
              <MenuItem value={1}>1 Guest</MenuItem>
              <MenuItem value={2}>2 Guests</MenuItem>
            </Select>
            {error && <FormHelperText>Number of guests is required for King Deluxe Room</FormHelperText>}
          </FormControl>
        )}

        {/* Check-in Date Picker */}
        <DatePicker
          label="Check-in Date"
          value={checkInDate}
          onChange={handleCheckInDateChange}
          renderInput={(params) => <TextField {...params} fullWidth />}
        />

        {/* Check-out Date Picker */}
        <DatePicker
          label="Check-out Date"
          value={checkOutDate}
          onChange={handleCheckOutDateChange}
          renderInput={(params) => <TextField {...params} fullWidth />}
        />

        {/* Submit Button */}
        <Button
          variant="contained"
          color="primary"
          onClick={handleSubmit}
          sx={{ width: '100%', padding: 1.5 }}
        >
          Submit Booking
        </Button>
      </Box>
    </LocalizationProvider>
  );
}
