import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Paper, List, ListItem, ListItemText, CircularProgress } from "@mui/material";
import { useNavigate } from "react-router-dom";

const ConfirmationPage = () => {
  const [bookingDetails, setBookingDetails] = useState(null);
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBookingDetails = async () => {
      const userId = localStorage.getItem("userId");
      const bookingId = localStorage.getItem("bookingId");

      if (!userId || !bookingId) {
        alert("User or booking ID not found");
        return;
      }

      try {
        // Fetch booking details for the user
        const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
        const data = await response.json();

        if (response.ok) {
          const latestBooking = data.bookings[data.bookings.length - 1]; // Get the latest booking
          setBookingDetails(latestBooking);

          // Fetch services for this booking using bookingId
          const servicesResponse = await fetch(
            `http://localhost:5000/api/services/${bookingId}`
          );
          
          const servicesData = await servicesResponse.json();
          console.log("Services Response:", servicesData);

          if (servicesResponse.ok) {
            setServices(servicesData.services || []); // Ensure services is an array
          } else {
            setError("Failed to fetch services");
          }
        } else {
          setError("Failed to fetch booking details");
        }
      } catch (err) {
        setError("Error fetching data");
      } finally {
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6" color="error">
          {error}
        </Typography>
      </Box>
    );
  }

  if (!bookingDetails) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6">No booking found.</Typography>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: "url('./background2.jpg')", // Background image
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        padding: 4,
      }}
    >
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={8} md={6}>
          <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
              Booking Confirmation
            </Typography>

            <Typography variant="h6" color="primary" gutterBottom>
              Booking Details
            </Typography>
            <List>
              <ListItem>
                <ListItemText primary="Room Type" secondary={bookingDetails.roomType} />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Check-in Date"
                  secondary={new Date(bookingDetails.checkInDate).toLocaleDateString()}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Check-out Date"
                  secondary={new Date(bookingDetails.checkOutDate).toLocaleDateString()}
                />
              </ListItem>
              <ListItem>
                <ListItemText primary="Number of Guests" secondary={bookingDetails.guests} />
              </ListItem>
            </List>

            <Typography variant="h6" color="primary" gutterBottom sx={{ marginTop: 3 }}>
              Services Booked
            </Typography>
            <List>
              {services.length ? (
                services.map((service, index) => (
                  <ListItem key={index}>
                    <ListItemText
                      primary={"Food Bundle"}
                      secondary={service.food.bundle || "No Food Bundle"}
                    />
                  </ListItem>                
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="No services booked" />
                </ListItem>
              )}
            </List>

            <Box sx={{ display: "flex", justifyContent: "center", marginTop: 4 }}>
              <Button variant="contained" color="primary" onClick={() => navigate("/Book-Now")}>
                Book Another Room
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ConfirmationPage;
