import React, { useState } from "react";
import { Box, TextField, Button, Typography, Alert, Snackbar } from "@mui/material";

function ContactUsPage() {
    const [message, setMessage] = useState("");
    const [snackbar, setSnackbar] = useState({ open: false, message: "", severity: "info" });

    const handleSendMessage = async () => {
        if (!message) {
            setSnackbar({ open: true, message: "Please fill out all fields.", severity: "warning" });
            return;
        }

        const email = localStorage.getItem("email");  // Retrieve email from localStorage

        if (!email) {
            setSnackbar({ open: true, message: "Email not found.", severity: "warning" });
            return;
        }

        try {
            const response = await fetch("http://localhost:5000/api/contact", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Email": email,  // Send email from localStorage in headers
                },
                body: JSON.stringify({ message }),
            });

            if (!response.ok) {
                throw new Error("Failed to send message.");
            }

            setSnackbar({ open: true, message: "Message sent successfully.", severity: "success" });
            setMessage("");
        } catch (error) {
            setSnackbar({ open: true, message: error.message || "Error occurred.", severity: "error" });
        }
    };

    const handleCloseSnackbar = () => {
        setSnackbar({ ...snackbar, open: false });
    };

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                padding: "15px 30px",
            }}
        >
            <Box
                sx={{
                    marginBottom: "20px",
                    backgroundColor: "red",
                    color: "white",
                    padding: "20px",
                    borderRadius: "8px",
                }}
            >
                <Typography variant="h4">Contact Us</Typography>
            </Box>

            <Box
                sx={{
                    border: "1px solid #ccc",
                    padding: "16px",
                    borderRadius: "8px",
                    maxWidth: "600px",
                    margin: "auto",
                    backgroundColor: "#f9f9f9",
                }}
            >
                <Typography variant="h6" sx={{ fontWeight: "bold" }}>Mailing Address</Typography>
                <Typography variant="body1" sx={{ marginTop: "8px" }}>
                    Egypt-Japan University for Science and Technology (E-JUST)
                </Typography>
                <Typography variant="body1">
                    P.O. Box 179, New Borg El-Arab City Postal Code 21934, Alexandria, Egypt
                </Typography>
                <Typography variant="body1">Call Center: 16448</Typography>
                <Typography variant="body1">Fax: 03-4600004</Typography>

                <Box sx={{ marginTop: "20px" }}>
                    <TextField
                        fullWidth
                        label="Your Message"
                        variant="outlined"
                        multiline
                        rows={4}
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        sx={{ marginBottom: "16px" }}
                    />
                    <Button variant="contained" color="primary" onClick={handleSendMessage}>
                        Send Message
                    </Button>
                </Box>
            </Box>

            <Snackbar
                open={snackbar.open}
                autoHideDuration={5000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
            >
                <Alert onClose={handleCloseSnackbar} severity={snackbar.severity}>
                    {snackbar.message}
                </Alert>
            </Snackbar>
        </Box>
    );
}

export default ContactUsPage;
