import * as React from 'react';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { Box, TextField } from '@mui/material';

export default function CustomSlotPropsCallback() {
    const [checkInDate, setCheckInDate] = React.useState(null);  // State for check-in date
    const [checkOutDate, setCheckOutDate] = React.useState(null); // State for check-out date

    // Handle check-in date change
    const handleCheckInDateChange = (newDate) => {
        setCheckInDate(newDate);
    };

    // Handle check-out date change
    const handleCheckOutDateChange = (newDate) => {
        setCheckOutDate(newDate);
    };
    const handleSubmit = async () => {
        if (!checkInDate || !checkOutDate) {
            alert("Both check-in and check-out dates are required.");
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/booking', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    roomType,
                    guests: 2, // Example guests count, modify as needed
                    checkInDate,
                    checkOutDate,
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                alert(data.message || 'An error occurred');
            } else {
                alert('Booking successful!');
                console.log('Booking details:', data.booking);
            }
        } catch (error) {
            console.error('Error during submission:', error);
            alert('Something went wrong. Please try again later.');
        }
    };

    return (
        <LocalizationProvider dateAdapter={AdapterDayjs}>
            <Box
                sx={{
                    paddingLeft: 0,
                    flexDirection: 'column',
                    gap: 2,
                    alignItems: 'center',
                    marginTop: '20px',
                }}
            >
                {/* Check-in Date Picker */}
                <DatePicker
                    label="Check-in Date"
                    value={checkInDate}
                    onChange={handleCheckInDateChange}
                    slotProps={{
                        openPickerIcon: (ownerState) => ({
                            color: ownerState.open ? 'secondary' : 'primary',
                        }),
                    }}
                    renderInput={(params) => <TextField {...params} />}
                />

                {/* Check-out Date Picker */}
                <DatePicker
                    label="Check-out Date"
                    value={checkOutDate}
                    onChange={handleCheckOutDateChange}
                    slotProps={{
                        openPickerIcon: (ownerState) => ({
                            color: ownerState.open ? 'secondary' : 'primary',
                        }),
                    }}
                    renderInput={(params) => <TextField {...params} />}
                />
            </Box>
        </LocalizationProvider>
    );
}
