import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Grid, Paper, List, ListItem, ListItemText, CircularProgress } from "@mui/material";
import { useNavigate } from "react-router-dom";

const ConfirmationPage = () => {
  const [bookingDetails, setBookingDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBookingDetails = async () => {
      const userId = localStorage.getItem("userId");
      const bookingId = localStorage.getItem("bookingId"); // Retrieve booking ID
      if (!userId || !bookingId) {
        alert("User or booking not found");
        return;
      }

      try {
        const response = await fetch(`http://localhost:5000/api/booking/${userId}/${bookingId}`);
        const data = await response.json();

        if (response.ok) {
          setBookingDetails(data.booking); // Set booking details in state
        } else {
          setError("Failed to fetch booking details");
        }
      } catch (err) {
        setError("Error fetching booking details");
      } finally {
        setLoading(false);
      }
    };

    fetchBookingDetails();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", minHeight: "100vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6" color="error">
          {error}
        </Typography>
      </Box>
    );
  }

  if (!bookingDetails) {
    return (
      <Box sx={{ textAlign: "center", marginTop: 3 }}>
        <Typography variant="h6">No booking found.</Typography>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundImage: "url('./background2.jpg')", // Path to your image in the public folder
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        padding: 4,
      }}
    >
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={8} md={6}>
          <Paper sx={{ padding: 3, backgroundColor: "rgba(255, 255, 255, 0.8)" }}>
            <Typography variant="h4" align="center" sx={{ marginBottom: 2 }}>
              Booking Confirmation
            </Typography>
            <Typography variant="h6" color="primary" gutterBottom>
              Booking Details
            </Typography>
            <List>
              <ListItem>
                <ListItemText
                  primary="Room Type"
                  secondary={bookingDetails.roomType}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Check-in Date"
                  secondary={new Date(bookingDetails.checkInDate).toLocaleDateString()}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Check-out Date"
                  secondary={new Date(bookingDetails.checkOutDate).toLocaleDateString()}
                />
              </ListItem>
              <ListItem>
                <ListItemText
                  primary="Number of Guests"
                  secondary={bookingDetails.guests}
                />
              </ListItem>
            </List>

            <Typography variant="h6" color="primary" gutterBottom sx={{ marginTop: 3 }}>
              Services Booked
            </Typography>
            <List>
              {bookingDetails.services?.length ? (
                bookingDetails.services.map((service, index) => (
                  <ListItem key={index}>
                    <ListItemText
                      primary={service.name}
                      secondary={`Price: ${service.price}`}
                    />
                  </ListItem>
                ))
              ) : (
                <ListItem>
                  <ListItemText primary="No services booked" />
                </ListItem>
              )}
            </List>

            <Box sx={{ display: "flex", justifyContent: "center", marginTop: 4 }}>
              <Button variant="contained" color="primary" onClick={() => navigate("/Book-Now")}>
                Book Another Room
              </Button>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ConfirmationPage;
