import React, { useState } from "react";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { TextField, Dialog, DialogTitle, DialogContent, DialogActions, List, ListItem, ListItemText } from "@mui/material";

export function SelectLabelsDouble() {
  const [roomType, setRoomType] = useState("");
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const [guests] = useState(2);
  const [error, setError] = useState(false);

  // State for the modal
  const [open, setOpen] = useState(false);
  const [bookings, setBookings] = useState([]);

  // Handle room type change
  const handleRoomChange = (event) => {
    setRoomType(event.target.value);
    setError(false);
  };

  // Handle check-in date change
  const handleCheckInDateChange = (newDate) => {
    setCheckInDate(newDate);
  };

  // Handle check-out date change
  const handleCheckOutDateChange = (newDate) => {
    setCheckOutDate(newDate);
  };

  // Handle submit button click
  const handleSubmit = async () => {
    if (!roomType || !checkInDate || !checkOutDate) {
      setError(true);
      alert("Please complete all fields");
      return;
    }

    const userId = localStorage.getItem("userId");

    const requestBody = {
      roomType,
      checkInDate: checkInDate.toISOString(),
      checkOutDate: checkOutDate.toISOString(),
      guests,
      userId,
    };

    try {
      const response = await fetch("http://localhost:5000/api/booking/details", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Booking details submitted successfully!");
      } else {
        alert(data.message || "Failed to submit booking details");
      }
    } catch (error) {
      console.error("Error:", error.message);
      alert(error.message || "Failed to submit booking details");
    }
  };

  // Handle "Show Bookings" button click
  const handleShowBookings = async () => {
    const userId = localStorage.getItem("userId");

    if (!userId) {
      alert("User not logged in");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/booking/${userId}`);
      const data = await response.json();

      if (response.ok) {
        setBookings(data.bookings); // Assuming `data.bookings` contains the list of bookings
        setOpen(true); // Open the modal
      } else {
        alert(data.message || "Failed to fetch bookings");
      }
    } catch (error) {
      console.error("Error:", error.message);
      alert(error.message || "Failed to fetch bookings");
    }
  };

  return (
    <Box>
      {/* Room Type Selection */}
      <FormControl sx={{ m: 1, minWidth: 400 }}>
        <InputLabel id="room-type-label">Room Type</InputLabel>
        <Select
          labelId="room-type-label"
          id="room-type-select"
          value={roomType}
          label="Room Type"
          onChange={handleRoomChange}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="doubleDuplex">Double Duplex Room</MenuItem>
          <MenuItem value="singleRoom">Single Room</MenuItem>
          <MenuItem value="suiteRoom">Suite Room</MenuItem>
        </Select>
        <FormHelperText>Choose your room type</FormHelperText>
      </FormControl>

      {/* Date Pickers */}
      <LocalizationProvider dateAdapter={AdapterDayjs}>
        <Box sx={{ paddingTop: 2 }}>
          <DatePicker
            label="Check-in Date"
            value={checkInDate}
            onChange={handleCheckInDateChange}
            renderInput={(params) => <TextField {...params} />}
          />

          <DatePicker
            label="Check-out Date"
            value={checkOutDate}
            onChange={handleCheckOutDateChange}
            renderInput={(params) => <TextField {...params} />}
          />
        </Box>
      </LocalizationProvider>

      {/* Buttons */}
      <Box sx={{ display: "flex", gap: 2, marginTop: 2 }}>
        {/* Submit Button */}
        <Button onClick={handleSubmit} variant="contained" color="primary">
          Submit Dates
        </Button>

        {/* Show Bookings Button */}
        <Button
          variant="contained"
          sx={{
            backgroundColor: "red",
            color: "white",
            "&:hover": { backgroundColor: "darkred" },
          }}
          onClick={handleShowBookings}
        >
          Show Bookings
        </Button>
      </Box>

      {/* Error Message */}
      {error && <FormHelperText error>All fields are required!</FormHelperText>}

      {/* Modal for Bookings */}
      <Dialog open={open} onClose={() => setOpen(false)} fullWidth>
        <DialogTitle>My Bookings</DialogTitle>
        <DialogContent>
          {bookings.length > 0 ? (
            <List>
              {bookings.map((booking, index) => (
                <ListItem key={index}>
                  <ListItemText
                    primary={`Room: ${booking.roomType}`}
                    secondary={`Check-in: ${booking.checkInDate}, Check-out: ${booking.checkOutDate}`}
                  />
                </ListItem>
              ))}
            </List>
          ) : (
            <p>No bookings found</p>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpen(false)} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}
