import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';

export default function SelectLabelsSingle() {
    const [roomType, setRoomType] = React.useState('');
    const [guests, setGuests] = React.useState('');
    const [error, setError] = React.useState(false);

    const handleRoomChange = (event) => {
        setRoomType(event.target.value);
        setGuests('');
        setError(false);
    };

    const handleGuestsChange = (event) => {
        setGuests(event.target.value);
    };

    const handleSubmit = async () => {
        if (!roomType || !guests) {
            setError(true);
        } else {
            setError(false);
            const response = await fetch("http://localhost:5000/api/booking/details", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ roomType, guests, checkInDate: "someDate", checkOutDate: "someDate", userId: "someUserId" }), // Replace with actual dates and userId
            });

            const data = await response.json();
            if (response.ok) {
                console.log(`Booking details: Room Type: ${roomType}, Number of Guests: ${guests}`);
            } else {
                alert(data.message || 'Failed to submit the booking details');
            }
        }
    };

    return (
        <Box>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    id="room-type-select"
                    value={roomType}
                    label="Room Type"
                    onChange={handleRoomChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value="kingDeluxe">King Deluxe Room</MenuItem>
                    <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
                </Select>
                <FormHelperText>Choose your room type</FormHelperText>
            </FormControl>

            <FormControl sx={{ m: 1, minWidth: 400 }} error={error}>
                <InputLabel id="guests-label">Number of Guests</InputLabel>
                <Select
                    labelId="guests-label"
                    id="guests-select"
                    value={guests}
                    label="Number of Guests"
                    onChange={handleGuestsChange}
                >
                    <MenuItem value="">
                        <em></em>
                    </MenuItem>
                    <MenuItem value={1}>1</MenuItem>
                    <MenuItem value={2}>2</MenuItem>
                    <MenuItem value={3}>3</MenuItem>
                </Select>
                {error && <FormHelperText>Guests number is required</FormHelperText>}
            </FormControl>

            <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center' }}>
                <Button variant="contained" onClick={handleSubmit}>Submit</Button>
            </Box>
        </Box>
    );
}
