// src/components/signin.js
import * as React from 'react';
import { AppProvider } from '@toolpad/core/AppProvider';
import { SignInPage } from '@toolpad/core/SignInPage';
import { useTheme } from '@mui/material/styles';
import { useNavigate } from 'react-router-dom';

const providers = [{ id: 'credentials', name: 'Credentials' }];

const BRANDING = {
    logo: (
        <img
            src="./ejustLogoSignin.png"
            alt="E-JUST logo"
            style={{ height: 24 }}
        />
    ),
    title: 'Sign in to E-JUST Accommodation', // Updated title
};

export default function BrandingSignInPage() {
    const theme = useTheme();
    const navigate = useNavigate();

    const signIn = async (provider, email, password) => {
        try {
            const response = await fetch("http://localhost:5000/api/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                console.log("Login successful:", data);
                navigate('/room-Type'); // Redirect to the roomtype page
            } else {
                console.error("Login failed:", data.message);
                alert(data.message || "Login failed. Please try again.");
            }
        } catch (error) {
            console.error("Error during login:", error);
            alert("An error occurred. Please try again.");
        }
    };

    return (
        <AppProvider branding={BRANDING} theme={theme}>
            <SignInPage
                signIn={(provider, email, password) => signIn(provider, email, password)}
                providers={providers}
                slotProps={{
                    emailField: { autoFocus: false },
                    onSubmit: async (values) => {
                        const { email, password } = values;
                        await signIn(providers[0], email, password);
                    },
                }}
            />
        </AppProvider>
    );
}
