import { Box, Button } from '@mui/material';
import React, { useState } from 'react';
import SelectLabelsfood from "./components/selectBoxfood";
import SelectLabelscourt from './components/selectBoxcourts';
import CourtDateSelect from "./components/dateSelectCourts";
import SelectLabelsGym from "./components/selectBoxGym";
import GymDateSelect from "./components/dateSelectCourts";

function Bookservices() {
    const [foodChoice, setFoodChoice] = useState('');
    const [courtChoice, setCourtChoice] = useState('');
    const [courtDate, setCourtDate] = useState('');
    const [gymChoice, setGymChoice] = useState('');
    const [gymDate, setGymDate] = useState('');

    const handleSubmit = () => {
        const bookingDetails = {
            food: foodChoice,
            court: { type: courtChoice, date: courtDate },
            gym: { type: gymChoice, date: gymDate },
        };
        console.log("Booking Details:", bookingDetails);
        // Add API call or further processing logic here
    };

    return (
        <Box
            sx={{
                minHeight: "100vh",
                backgroundColor: "white",
                backgroundSize: "cover",
                padding: "15px 30px",
                backgroundRepeat: "no-repeat",
            }}
        >
            <Box
                sx={{
                    marginBottom: '20px',
                    backgroundColor: 'red',
                    color: 'white',
                    padding: '1px',
                    borderRadius: '8px',
                }}
            >
                <div style={{ textAlign: 'center', marginTop: '20px' }}>
                    <h1>E-Just services</h1>
                </div>
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Book your food bundle</h3>
                <SelectLabelsfood onChange={(value) => setFoodChoice(value)} />
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Book your sport court</h3>
                <SelectLabelscourt onChange={(value) => setCourtChoice(value)} />
                <CourtDateSelect onChange={(value) => setCourtDate(value)} />
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    boxShadow: 3,
                    padding: '20px',
                    borderRadius: '8px',
                    backgroundColor: 'white',
                    width: '100%',
                    maxWidth: '600px',
                    marginLeft: '20px',
                    marginTop: '20px',
                }}
            >
                <h3>Book your gym time</h3>
                <SelectLabelsGym onChange={(value) => setGymChoice(value)} />
                <GymDateSelect onChange={(value) => setGymDate(value)} />
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    justifyContent: 'center',
                    marginTop: '30px',
                }}
            >
                <Button
                    variant="contained"
                    color="primary"
                    onClick={handleSubmit}
                >
                    Submit All Choices
                </Button>
            </Box>
        </Box>
    );
}

export default Bookservices;
