import React, { useState } from 'react';
import { Box, Button, FormControl, FormHelperText, InputLabel, MenuItem, Select, TextField } from '@mui/material';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';

export default function SelectLabels() {
    const [formData, setFormData] = useState({
        roomType: '',
        guests: '',
        checkInDate: null,
        checkOutDate: null,
    });
    const [error, setError] = useState(false);

    const handleChange = (field) => (event) => {
        setFormData({ ...formData, [field]: event.target ? event.target.value : event });
    };

    const handleSubmit = () => {
        if (formData.roomType === 'kingDeluxe' && !formData.guests) {
            setError(true);
        } else {
            setError(false);
            console.log('Form Data:', formData);
        }
    };

    return (
        <Box sx={{ marginTop: '20px' }}>
            <FormControl sx={{ m: 1, minWidth: 400 }}>
                <InputLabel id="room-type-label">Room Type</InputLabel>
                <Select
                    labelId="room-type-label"
                    value={formData.roomType}
                    onChange={handleChange('roomType')}
                >
                    <MenuItem value=""><em>None</em></MenuItem>
                    <MenuItem value="kingDeluxe">King Deluxe Room</MenuItem>
                    <MenuItem value="deluxeDouble">Deluxe Double Room</MenuItem>
                </Select>
                <FormHelperText>Choose your room type</FormHelperText>
            </FormControl>

            {formData.roomType === 'kingDeluxe' && (
                <FormControl sx={{ m: 1, minWidth: 400 }} error={error}>
                    <InputLabel id="guests-label">Number of Guests</InputLabel>
                    <Select
                        labelId="guests-label"
                        value={formData.guests}
                        onChange={handleChange('guests')}
                    >
                        <MenuItem value=""><em>None</em></MenuItem>
                        <MenuItem value={1}>1 Guest</MenuItem>
                        <MenuItem value={2}>2 Guests</MenuItem>
                    </Select>
                    {error && <FormHelperText>Number of guests is required</FormHelperText>}
                </FormControl>
            )}

            <LocalizationProvider dateAdapter={AdapterDayjs}>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, marginTop: '20px' }}>
                    <DatePicker
                        label="Check-in Date"
                        value={formData.checkInDate}
                        onChange={handleChange('checkInDate')}
                        renderInput={(params) => <TextField {...params} />}
                    />
                    <DatePicker
                        label="Check-out Date"
                        value={formData.checkOutDate}
                        onChange={handleChange('checkOutDate')}
                        renderInput={(params) => <TextField {...params} />}
                    />
                </Box>
            </LocalizationProvider>
        </Box>
    );
}
